   513‚Üí    private function is_duplicate_description($description, $current_product_id) {
   514‚Üí        global $wpdb;
   515‚Üí        
   516‚Üí        // Calcola hash della descrizione
   517‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
