   520‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   521‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   522‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   523‚Üí            $hash,
   524‚Üí            $current_product_id
   525‚Üí        ));
   526‚Üí        
   527‚Üí        if ($exists > 0) {
   528‚Üí            return true;
   529‚Üí        }
   530‚Üí        
   531‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   532‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   533‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   534‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   535‚Üí            WHERE p.post_type = 'product'
   536‚Üí            AND p.ID != %d
   537‚Üí            AND (
   538‚Üí                p.post_excerpt LIKE %s
   539‚Üí                OR p.post_content LIKE %s
   540‚Üí                OR pm.meta_value LIKE %s
   541‚Üí            )",
   542‚Üí            $current_product_id,
   543‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   544‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   545‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   546‚Üí        ));
   547‚Üí        
   548‚Üí        return $similar_products > 0;
