   712‚Üí    private function export_descriptions() {
   713‚Üí        global $wpdb;
   714‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   715‚Üí        
   716‚Üí        $results = $wpdb->get_results(
   717‚Üí            "SELECT d.*, p.post_title 
   718‚Üí             FROM $table_name d
   719‚Üí             LEFT JOIN {$wpdb->posts} p ON d.product_id = p.ID
   720‚Üí             ORDER BY d.created_at DESC"
   721‚Üí        );
   722‚Üí        
   723‚Üí        header('Content-Type: text/csv; charset=utf-8');
   724‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   725‚Üí        
   726‚Üí        $output = fopen('php://output', 'w');
   727‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   728‚Üí        
   729‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   730‚Üí        
   731‚Üí        foreach ($results as $row) {
   732‚Üí            fputcsv($output, array(
   733‚Üí                $row->id,
   734‚Üí                $row->product_id,
   735‚Üí                $row->post_title,
   736‚Üí                $row->description,
   737‚Üí                $row->hash,
   738‚Üí                $row->created_at
   739‚Üí            ));
   740‚Üí        }
   741‚Üí        
   742‚Üí        fclose($output);
   743‚Üí        exit;
   744‚Üí    }
   745‚Üí}
