   445‚Üí    /**
   446‚Üí     * Costruisci prompt per OpenAI
   447‚Üí     */
   448‚Üí    private function build_prompt($product_data, $variation = false) {
   449‚Üí        // Rileva lingua corrente per WPML
   450‚Üí        $current_lang = defined('ICL_LANGUAGE_CODE') ? ICL_LANGUAGE_CODE : 'it';
   451‚Üí        
   452‚Üí        // Prompt personalizzato per lingua
   453‚Üí        $prompts = array(
   454‚Üí            'it' => "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n",
   455‚Üí            'en' => "Create a professional description of maximum 5 lines for this luxury watch from a jewelry store:\n\n",
   456‚Üí            'fr' => "Cr√©ez une description professionnelle de maximum 5 lignes pour cette montre de luxe d'une bijouterie:\n\n",
   457‚Üí            'de' => "Erstellen Sie eine professionelle Beschreibung von maximal 5 Zeilen f√ºr diese Luxusuhr aus einem Juweliergesch√§ft:\n\n"
   458‚Üí        );
   459‚Üí        
   460‚Üí        $prompt = isset($prompts[$current_lang]) ? $prompts[$current_lang] : $prompts['it'];
   461‚Üí        
   462‚Üí        // Estrai brand dalla categoria prodotto
   463‚Üí        $brand = '';
   464‚Üí        if (!empty($product_data['categories'])) {
   465‚Üí            $brand = $product_data['categories'][0];
   466‚Üí        }
   467‚Üí        
   468‚Üí        $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
   469‚Üí        if ($brand) $prompt .= "MARCA: " . $brand . "\n";
   470‚Üí        
   471‚Üí        // Aggiungi categoria prodotto per personalizzare il linguaggio
   472‚Üí        if (!empty($product_data['product_groups'])) {
   473‚Üí            $group = $product_data['product_groups'][0];
   474‚Üí            $prompt .= "CATEGORIA: " . ucfirst($group) . "\n";
   475‚Üí            
   476‚Üí            // Personalizza approccio per categoria
   477‚Üí            if ($group === 'vintage') {
   478‚Üí                $prompt .= "STILE: Orologio d'epoca di valore storico\n";
   479‚Üí            } elseif ($group === 'premium') {
   480‚Üí                $prompt .= "STILE: Pezzo di alta orologeria di lusso\n";
   481‚Üí            } elseif ($group === 'accessories') {
   482‚Üí                $prompt .= "STILE: Accessorio per orologeria\n";
   483‚Üí            }
   484‚Üí        }
   485‚Üí        
   486‚Üí        // Campi specifici ACF del sito
   487‚Üí        $meta = $product_data['meta'];
   488‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   489‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
   490‚Üí        if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
   491‚Üí        if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
   492‚Üí        if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
   493‚Üí        if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
   494‚Üí        if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
   495‚Üí        if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";
   496‚Üí        
   497‚Üí        // Accessori e garanzia
   498‚Üí        $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
   499‚Üí        $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
   500‚Üí        $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
   501‚Üí        $prompt .= "GARANZIA: " . ucfirst($warranty) . "\n";
   502‚Üí        
   503‚Üí        if (!empty($meta['watch_notes'])) {
   504‚Üí            $prompt .= "NOTE: " . $meta['watch_notes'] . "\n";
   505‚Üí        }
   506‚Üí        
   507‚Üí        $prompt .= "\nCREA UNA DESCRIZIONE CHE:\n";
   508‚Üí        $prompt .= "- Introduca elegantemente l'orologio con marca e modello\n";
   509‚Üí        $prompt .= "- Evidenzi le caratteristiche tecniche pi√π importanti (cassa, movimento, dimensioni)\n";
   510‚Üí        $prompt .= "- Specifichi chiaramente le condizioni (nuovo/usato)\n";
   511‚Üí        $prompt .= "- Indichi disponibilit√† di scatola e tipo di garanzia\n";
   512‚Üí        $prompt .= "- Usi un linguaggio professionale da gioielleria di lusso\n";
   513‚Üí        
   514‚Üí        if ($variation) {
   515‚Üí            $prompt .= "\nIMPORTANTE: Crea una descrizione completamente diversa dalle precedenti, con struttura e terminologia alternative.";
   516‚Üí        }
   517‚Üí        
   518‚Üí        return $prompt;
   519‚Üí    }
