   521‚Üí    /**
   522‚Üí     * Verifica se la descrizione √® duplicata
   523‚Üí     */
   524‚Üí    private function is_duplicate_description($description, $current_product_id) {
   525‚Üí        global $wpdb;
   526‚Üí        
   527‚Üí        // Calcola hash della descrizione
   528‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   529‚Üí        
   530‚Üí        // Cerca nel database delle descrizioni generate
   531‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   532‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   533‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   534‚Üí            $hash,
   535‚Üí            $current_product_id
   536‚Üí        ));
   537‚Üí        
   538‚Üí        if ($exists > 0) {
   539‚Üí            return true;
   540‚Üí        }
   541‚Üí        
   542‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
