   723‚Üí    private function export_descriptions() {
   724‚Üí        global $wpdb;
   725‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   726‚Üí        
   727‚Üí        $results = $wpdb->get_results(
   728‚Üí            "SELECT d.*, p.post_title 
   729‚Üí             FROM $table_name d
   730‚Üí             LEFT JOIN {$wpdb->posts} p ON d.product_id = p.ID
   731‚Üí             ORDER BY d.created_at DESC"
   732‚Üí        );
   733‚Üí        
   734‚Üí        header('Content-Type: text/csv; charset=utf-8');
   735‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   736‚Üí        
   737‚Üí        $output = fopen('php://output', 'w');
   738‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   739‚Üí        
   740‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   741‚Üí        
   742‚Üí        foreach ($results as $row) {
   743‚Üí            fputcsv($output, array(
   744‚Üí                $row->id,
   745‚Üí                $row->product_id,
   746‚Üí                $row->post_title,
   747‚Üí                $row->description,
   748‚Üí                $row->hash,
   749‚Üí                $row->created_at
   750‚Üí            ));
   751‚Üí        }
   752‚Üí        
   753‚Üí        fclose($output);
   754‚Üí        exit;
   755‚Üí    }
   756‚Üí}
