   397‚Üí    /**
   398‚Üí     * Genera descrizione con OpenAI
   399‚Üí     */
   400‚Üí    private function generate_description_with_openai($product_data, $variation = false) {
   401‚Üí        $api_key = get_option('aiwd_openai_key');
   402‚Üí        if (!$api_key) {
   403‚Üí            return new WP_Error('no_api_key', 'Chiave API OpenAI non configurata');
   404‚Üí        }
   405‚Üí        
   406‚Üí        $prompt = $this->build_prompt($product_data, $variation);
   407‚Üí        
   408‚Üí        // Rileva lingua corrente per WPML
   409‚Üí        $current_lang = defined('ICL_LANGUAGE_CODE') ? ICL_LANGUAGE_CODE : 'it';
   410‚Üí        
   411‚Üí        // System messages multilingua
   412‚Üí        $system_messages = array(
   413‚Üí            'it' => 'Sei un esperto di orologi di lusso che crea descrizioni concise e professionali per un sito di gioielleria italiana.',
   414‚Üí            'en' => 'You are a luxury watch expert who creates concise and professional descriptions for an Italian jewelry website.',
   415‚Üí            'fr' => 'Vous √™tes un expert en montres de luxe qui cr√©e des descriptions concises et professionnelles pour un site de bijouterie italien.',
   416‚Üí            'de' => 'Sie sind ein Luxusuhren-Experte, der pr√§gnante und professionelle Beschreibungen f√ºr eine italienische Schmuck-Website erstellt.'
   417‚Üí        );
   418‚Üí        
   419‚Üí        $system_message = isset($system_messages[$current_lang]) ? $system_messages[$current_lang] : $system_messages['it'];
   420‚Üí        
   421‚Üí        $response = wp_remote_post('https://api.openai.com/v1/chat/completions', array(
   422‚Üí            'headers' => array(
   423‚Üí                'Authorization' => 'Bearer ' . $api_key,
   424‚Üí                'Content-Type' => 'application/json'
   425‚Üí            ),
   426‚Üí            'body' => json_encode(array(
   427‚Üí                'model' => get_option('aiwd_model', 'gpt-3.5-turbo'),
   428‚Üí                'messages' => array(
   429‚Üí                    array(
   430‚Üí                        'role' => 'system',
   431‚Üí                        'content' => $system_message
   432‚Üí                    ),
   433‚Üí                    array(
   434‚Üí                        'role' => 'user',
   435‚Üí                        'content' => $prompt
   436‚Üí                    )
   437‚Üí                ),
   438‚Üí                'max_tokens' => 150,
   439‚Üí                'temperature' => $variation ? 0.8 : 0.7
   440‚Üí            )),
   441‚Üí            'timeout' => 30
   442‚Üí        ));
   443‚Üí        
   444‚Üí        if (is_wp_error($response)) {
   445‚Üí            return $response;
   446‚Üí        }
   447‚Üí        
   448‚Üí        $body = wp_remote_retrieve_body($response);
   449‚Üí        $data = json_decode($body, true);
   450‚Üí        
   451‚Üí        if (isset($data['choices'][0]['message']['content'])) {
   452‚Üí            return trim($data['choices'][0]['message']['content']);
   453‚Üí        }
   454‚Üí        
   455‚Üí        return new WP_Error('api_error', 'Errore nella risposta API');
   456‚Üí    }
