   458‚Üí    /**
   459‚Üí     * Costruisci prompt per OpenAI
   460‚Üí     */
   461‚Üí    private function build_prompt($product_data, $variation = false) {
   462‚Üí        // Rileva lingua corrente per WPML
   463‚Üí        $current_lang = defined('ICL_LANGUAGE_CODE') ? ICL_LANGUAGE_CODE : 'it';
   464‚Üí        
   465‚Üí        // Prompt personalizzato per lingua
   466‚Üí        $prompts = array(
   467‚Üí            'it' => "Crea una descrizione professionale di massimo 5 righe per questo orologio di lusso da gioielleria:\n\n",
   468‚Üí            'en' => "Create a professional description of maximum 5 lines for this luxury watch from a jewelry store:\n\n",
   469‚Üí            'fr' => "Cr√©ez une description professionnelle de maximum 5 lignes pour cette montre de luxe d'une bijouterie:\n\n",
   470‚Üí            'de' => "Erstellen Sie eine professionelle Beschreibung von maximal 5 Zeilen f√ºr diese Luxusuhr aus einem Juweliergesch√§ft:\n\n"
   471‚Üí        );
   472‚Üí        
   473‚Üí        $prompt = isset($prompts[$current_lang]) ? $prompts[$current_lang] : $prompts['it'];
   474‚Üí        
   475‚Üí        // Estrai brand dalla categoria prodotto
   476‚Üí        $brand = '';
   477‚Üí        if (!empty($product_data['categories'])) {
   478‚Üí            $brand = $product_data['categories'][0];
   479‚Üí        }
   480‚Üí        
   481‚Üí        $prompt .= "OROLOGIO: " . $product_data['name'] . "\n";
   482‚Üí        if ($brand) $prompt .= "MARCA: " . $brand . "\n";
   483‚Üí        
   484‚Üí        // Aggiungi categoria prodotto per personalizzare il linguaggio
   485‚Üí        if (!empty($product_data['product_groups'])) {
   486‚Üí            $group = $product_data['product_groups'][0];
   487‚Üí            $prompt .= "CATEGORIA: " . ucfirst($group) . "\n";
   488‚Üí            
   489‚Üí            // Personalizza approccio per categoria
   490‚Üí            if ($group === 'vintage') {
   491‚Üí                $prompt .= "STILE: Orologio d'epoca di valore storico\n";
   492‚Üí            } elseif ($group === 'premium') {
   493‚Üí                $prompt .= "STILE: Pezzo di alta orologeria di lusso\n";
   494‚Üí            } elseif ($group === 'accessories') {
   495‚Üí                $prompt .= "STILE: Accessorio per orologeria\n";
   496‚Üí            }
   497‚Üí        }
   498‚Üí        
   499‚Üí        // Campi specifici ACF del sito
   500‚Üí        $meta = $product_data['meta'];
   501‚Üí        if (!empty($meta['watch_reference'])) $prompt .= "REFERENZA: " . $meta['watch_reference'] . "\n";
   502‚Üí        if (!empty($meta['watch_production_year'])) $prompt .= "ANNO: " . $meta['watch_production_year'] . "\n";
   503‚Üí        if (!empty($meta['watch_case'])) $prompt .= "CASSA: " . $meta['watch_case'] . "\n";
   504‚Üí        if (!empty($meta['watch_dimension'])) $prompt .= "DIMENSIONI: " . $meta['watch_dimension'] . "mm\n";
   505‚Üí        if (!empty($meta['watch_movement'])) $prompt .= "MOVIMENTO: " . $meta['watch_movement'] . "\n";
   506‚Üí        if (!empty($meta['watch_dial'])) $prompt .= "QUADRANTE: " . $meta['watch_dial'] . "\n";
   507‚Üí        if (!empty($meta['watch_bracelet'])) $prompt .= "BRACCIALE: " . $meta['watch_bracelet'] . "\n";
   508‚Üí        if (!empty($meta['watch_status'])) $prompt .= "CONDIZIONI: " . $meta['watch_status'] . "\n";
   509‚Üí        
   510‚Üí        // Accessori e garanzia
   511‚Üí        $box = !empty($meta['watch_box']) && $meta['watch_box'] ? 'S√å' : 'NO';
   512‚Üí        $warranty = !empty($meta['watch_warranty']) ? $meta['watch_warranty'] : 'no';
   513‚Üí        $prompt .= "SCATOLA ORIGINALE: " . $box . "\n";
   514‚Üí        $prompt .= "GARANZIA: " . ucfirst($warranty) . "\n";
   515‚Üí        
   516‚Üí        if (!empty($meta['watch_notes'])) {
   517‚Üí            $prompt .= "NOTE: " . $meta['watch_notes'] . "\n";
   518‚Üí        }
   519‚Üí        
   520‚Üí        $prompt .= "\nCREA UNA DESCRIZIONE CHE:\n";
   521‚Üí        $prompt .= "- Introduca elegantemente l'orologio con marca e modello\n";
   522‚Üí        $prompt .= "- Evidenzi le caratteristiche tecniche pi√π importanti (cassa, movimento, dimensioni)\n";
   523‚Üí        $prompt .= "- Specifichi chiaramente le condizioni (nuovo/usato)\n";
   524‚Üí        $prompt .= "- Indichi disponibilit√† di scatola e tipo di garanzia\n";
   525‚Üí        $prompt .= "- Usi un linguaggio professionale da gioielleria di lusso\n";
   526‚Üí        
   527‚Üí        if ($variation) {
   528‚Üí            $prompt .= "\nIMPORTANTE: Crea una descrizione completamente diversa dalle precedenti, con struttura e terminologia alternative.";
   529‚Üí        }
   530‚Üí        
   531‚Üí        return $prompt;
   532‚Üí    }
