   534‚Üí    /**
   535‚Üí     * Verifica se la descrizione √® duplicata
   536‚Üí     */
   537‚Üí    private function is_duplicate_description($description, $current_product_id) {
   538‚Üí        global $wpdb;
   539‚Üí        
   540‚Üí        // Calcola hash della descrizione
   541‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   542‚Üí        
   543‚Üí        // Cerca nel database delle descrizioni generate
   544‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   545‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   546‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   547‚Üí            $hash,
   548‚Üí            $current_product_id
   549‚Üí        ));
   550‚Üí        
   551‚Üí        if ($exists > 0) {
   552‚Üí            return true;
   553‚Üí        }
   554‚Üí        
   555‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   556‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   557‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   558‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   559‚Üí            WHERE p.post_type = 'product'
   560‚Üí            AND p.ID != %d
   561‚Üí            AND (
   562‚Üí                p.post_excerpt LIKE %s
   563‚Üí                OR p.post_content LIKE %s
   564‚Üí                OR pm.meta_value LIKE %s
   565‚Üí            )",
   566‚Üí            $current_product_id,
   567‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   568‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   569‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   570‚Üí        ));
   571‚Üí        
   572‚Üí        return $similar_products > 0;
   573‚Üí    }
