   744‚Üí             ORDER BY d.created_at DESC"
   745‚Üí        );
   746‚Üí        
   747‚Üí        header('Content-Type: text/csv; charset=utf-8');
   748‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   749‚Üí        
   750‚Üí        $output = fopen('php://output', 'w');
   751‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   752‚Üí        
   753‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   754‚Üí        
   755‚Üí        foreach ($results as $row) {
   756‚Üí            fputcsv($output, array(
