   543‚Üí    /**
   544‚Üí     * Verifica se la descrizione √® duplicata
   545‚Üí     */
   546‚Üí    private function is_duplicate_description($description, $current_product_id) {
   547‚Üí        global $wpdb;
   548‚Üí        
   549‚Üí        // Calcola hash della descrizione
   550‚Üí        $hash = md5(strtolower(str_replace(' ', '', $description)));
   551‚Üí        
   552‚Üí        // Cerca nel database delle descrizioni generate
   553‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   554‚Üí        $exists = $wpdb->get_var($wpdb->prepare(
   555‚Üí            "SELECT COUNT(*) FROM $table_name WHERE hash = %s AND product_id != %d",
   556‚Üí            $hash,
   557‚Üí            $current_product_id
   558‚Üí        ));
   559‚Üí        
   560‚Üí        if ($exists > 0) {
   561‚Üí            return true;
   562‚Üí        }
   563‚Üí        
   564‚Üí        // Cerca nelle descrizioni esistenti dei prodotti
   565‚Üí        $similar_products = $wpdb->get_var($wpdb->prepare(
   566‚Üí            "SELECT COUNT(*) FROM {$wpdb->posts} p
   567‚Üí            JOIN {$wpdb->postmeta} pm ON p.ID = pm.post_id
   568‚Üí            WHERE p.post_type = 'product'
   569‚Üí            AND p.ID != %d
   570‚Üí            AND (
   571‚Üí                p.post_excerpt LIKE %s
   572‚Üí                OR p.post_content LIKE %s
   573‚Üí                OR pm.meta_value LIKE %s
   574‚Üí            )",
   575‚Üí            $current_product_id,
   576‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   577‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%',
   578‚Üí            '%' . $wpdb->esc_like(substr($description, 0, 50)) . '%'
   579‚Üí        ));
   580‚Üí        
   581‚Üí        return $similar_products > 0;
   582‚Üí    }
