   584‚Üí    /**
   585‚Üí     * Salva descrizione generata
   586‚Üí     */
   587‚Üí    private function save_generated_description($product_id, $description) {
   588‚Üí        global $wpdb;
   589‚Üí        
   590‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   591‚Üí        $wpdb->insert(
   592‚Üí            $table_name,
   593‚Üí            array(
   594‚Üí                'product_id' => $product_id,
   595‚Üí                'description' => $description,
   596‚Üí                'hash' => md5(strtolower(str_replace(' ', '', $description)))
   597‚Üí            )
   598‚Üí        );
   599‚Üí    }
