   601‚Üí    /**
   602‚Üí     * Pagina manutenzione
   603‚Üí     */
   604‚Üí    public function maintenance_page() {
   605‚Üí        global $wpdb;
   606‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   607‚Üí        
   608‚Üí        // Gestisci azioni
   609‚Üí        if (isset($_POST['action'])) {
   610‚Üí            if (!wp_verify_nonce($_POST['_wpnonce'], 'aiwd_maintenance')) {
   611‚Üí                wp_die('Nonce verification failed');
   612‚Üí            }
   613‚Üí            
