   742‚Üí    /**
   743‚Üí     * Esporta descrizioni in CSV
   744‚Üí     */
   745‚Üí    private function export_descriptions() {
   746‚Üí        global $wpdb;
   747‚Üí        $table_name = $wpdb->prefix . 'aiwd_descriptions';
   748‚Üí        
   749‚Üí        $results = $wpdb->get_results(
   750‚Üí            "SELECT d.*, p.post_title 
   751‚Üí             FROM $table_name d
   752‚Üí             LEFT JOIN {$wpdb->posts} p ON d.product_id = p.ID
   753‚Üí             ORDER BY d.created_at DESC"
   754‚Üí        );
   755‚Üí        
   756‚Üí        header('Content-Type: text/csv; charset=utf-8');
   757‚Üí        header('Content-Disposition: attachment; filename=aiwd-descriptions-' . date('Y-m-d') . '.csv');
   758‚Üí        
   759‚Üí        $output = fopen('php://output', 'w');
   760‚Üí        fprintf($output, chr(0xEF).chr(0xBB).chr(0xBF)); // BOM per UTF-8
   761‚Üí        
   762‚Üí        fputcsv($output, array('ID', 'Product ID', 'Product Name', 'Description', 'Hash', 'Created At'));
   763‚Üí        
   764‚Üí        foreach ($results as $row) {
   765‚Üí            fputcsv($output, array(
   766‚Üí                $row->id,
   767‚Üí                $row->product_id,
   768‚Üí                $row->post_title,
   769‚Üí                $row->description,
   770‚Üí                $row->hash,
   771‚Üí                $row->created_at
   772‚Üí            ));
   773‚Üí        }
   774‚Üí        
   775‚Üí        fclose($output);
   776‚Üí        exit;
   777‚Üí    }
   778‚Üí}
