/**
 * AI Watch Description Generator - Admin Styles
 */

/* Gruppo opzioni principale */
.aiwd-generator {
    background-color: #f8f9fa;
    border: 1px solid #e1e4e8;
    border-radius: 4px;
    padding: 15px !important;
    margin: 20px 0;
}

.aiwd-generator .form-field {
    margin: 0;
}

/* Pulsante genera */
#aiwd-generate-btn {
    margin-right: 10px;
}

#aiwd-generate-btn:disabled {
    opacity: 0.7;
    cursor: not-allowed;
}

/* Status e spinner */
.aiwd-status {
    display: inline-block;
    vertical-align: middle;
    margin-left: 10px;
}

.aiwd-status .spinner {
    float: none;
    margin: 0;
    vertical-align: middle;
}

.aiwd-status .dashicons {
    font-size: 20px;
    width: 20px;
    height: 20px;
    vertical-align: middle;
}

/* Risultato */
.aiwd-result {
    margin-top: 15px;
    padding: 15px;
    background-color: #ffffff;
    border: 1px solid #dcdcde;
    border-radius: 4px;
}

