    transition: opacity 0.3s;
    pointer-events: none;
}

#aiwd-generate-btn:hover::after {
    opacity: 1;
}

/* Loading state */
.aiwd-loading {
    position: relative;
    overflow: hidden;
}

.aiwd-loading::before {
    content: "";
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        90deg,
        transparent,
        rgba(255, 255, 255, 0.2),
        transparent
    );
    animation: loading 1.5s infinite;
}

@keyframes loading {
    0% {
        left: -100%;
    }
    100% {
        left: 100%;
    }
}

