const redis = require('redis');

let client;
let isConnected = false;

const initCache = async () => {
  try {
    client = redis.createClient({
      url: process.env.REDIS_URL || 'redis://localhost:6379',
      retry_strategy: (options) => {
        if (options.error && options.error.code === 'ECONNREFUSED') {
          console.error('Redis connection refused');
          return new Error('Redis connection refused');
        }
        if (options.total_retry_time > 1000 * 60 * 60) {
          return new Error('Redis retry time exhausted');
        }
        if (options.attempt > 10) {
          return undefined;
        }
        return Math.min(options.attempt * 100, 3000);
      }
    });

    client.on('error', (err) => {
      console.error('Redis error:', err);
      isConnected = false;
    });

    client.on('connect', () => {
      console.log('Redis connected');
      isConnected = true;
    });

    client.on('ready', () => {
      console.log('Redis ready');
      isConnected = true;
    });

    client.on('end', () => {
      console.log('Redis connection ended');
      isConnected = false;
    });

    await client.connect();
    return client;
  } catch (error) {
    console.error('Failed to initialize Redis:', error);
    isConnected = false;
    return null;
  }
};

const get = async (key) => {
  if (!isConnected || !client) {
    return null;
  }

  try {
    const data = await client.get(key);
    return data ? JSON.parse(data) : null;
  } catch (error) {
    console.error('Cache get error:', error);
    return null;
  }
};

const set = async (key, value, ttl = 3600) => {
  if (!isConnected || !client) {
    return false;
  }

  try {
    await client.setEx(key, ttl, JSON.stringify(value));
    return true;
  } catch (error) {
    console.error('Cache set error:', error);
    return false;
  }
};

const del = async (key) => {
  if (!isConnected || !client) {
    return false;
  }

  try {
    await client.del(key);
    return true;
  } catch (error) {
    console.error('Cache delete error:', error);
    return false;
  }
};

const flush = async () => {
  if (!isConnected || !client) {
    return false;
  }

  try {
    await client.flushAll();
    return true;
  } catch (error) {
    console.error('Cache flush error:', error);
    return false;
  }
};

// Cache keys generator
const keys = {
  activities: (filters = {}) => {
    const filterStr = Object.keys(filters).sort().map(k => `${k}:${filters[k]}`).join('|');
    return `activities:${filterStr || 'all'}`;
  },
  activity: (id) => `activity:${id}`,
  userActivities: (userId) => `user:${userId}:activities`,
  userSavedActivities: (userId) => `user:${userId}:saved`,
  userProfile: (userId) => `user:${userId}:profile`,
  aiResponse: (prompt) => `ai:${Buffer.from(prompt).toString('base64').slice(0, 50)}`,
  categories: () => 'categories:all'
};

module.exports = {
  initCache,
  get,
  set,
  del,
  flush,
  keys,
  isConnected: () => isConnected
};

