const { Pool } = require('pg');

// Optimized connection pool configuration
const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
  // Connection pool settings
  min: 5,                    // minimum pool size
  max: 20,                   // maximum pool size
  idleTimeoutMillis: 30000,  // close idle clients after 30 seconds
  connectionTimeoutMillis: 2000, // return error after 2 seconds if connection could not be established
  // Query timeout
  statement_timeout: 10000,  // cancel queries after 10 seconds
  // Keep-alive settings
  keepAlive: true,
  keepAliveInitialDelayMillis: 10000
});

// Database health monitoring
pool.on('connect', (client) => {
  console.log('New database client connected');
});

pool.on('error', (err, client) => {
  console.error('Database client error:', err);
});

pool.on('acquire', (client) => {
  console.log('Database client acquired from pool');
});

pool.on('remove', (client) => {
  console.log('Database client removed from pool');
});

// Query wrapper with error handling and logging
const query = async (text, params = []) => {
  const start = Date.now();
  const client = await pool.connect();

  try {
    const result = await client.query(text, params);
    const duration = Date.now() - start;

    // Log slow queries (> 1 second)
    if (duration > 1000) {
      console.warn(`Slow query detected (${duration}ms):`, text.slice(0, 100) + '...');
    }

    return result;
  } catch (error) {
    const duration = Date.now() - start;
    console.error(`Query failed after ${duration}ms:`, error.message);
    console.error('Query:', text.slice(0, 100) + '...');
    console.error('Params:', params);
    throw error;
  } finally {
    client.release();
  }
};

// Transaction wrapper
const transaction = async (callback) => {
  const client = await pool.connect();

  try {
    await client.query('BEGIN');
    const result = await callback(client);
    await client.query('COMMIT');
    return result;
  } catch (error) {
    await client.query('ROLLBACK');
    throw error;
  } finally {
    client.release();
  }
};

// Prepared statements for common queries
const preparedQueries = {
  // User queries
  getUserById: 'SELECT * FROM users WHERE id = $1',
  getUserByEmail: 'SELECT * FROM users WHERE email = $1',
  updateUserProfile: `
    UPDATE users 
    SET name = $2, child_name = $3, child_age = $4, updated_at = CURRENT_TIMESTAMP 
    WHERE id = $1 
    RETURNING *
  `,

  // Activity queries
  getActivities: `
    SELECT * FROM activities 
    WHERE ($1::VARCHAR IS NULL OR category = $1)
    AND ($2::INTEGER IS NULL OR age_min <= $2)
    AND ($3::INTEGER IS NULL OR age_max >= $3)
    AND ($4::INTEGER IS NULL OR duration <= $4)
    AND ($5::VARCHAR IS NULL OR difficulty = $5)
    ORDER BY created_at DESC
    LIMIT $6 OFFSET $7
  `,

  getActivityById: 'SELECT * FROM activities WHERE id = $1',

  // Saved activities
  saveActivity: `
    INSERT INTO saved_activities (user_id, activity_id) 
    VALUES ($1, $2) 
    ON CONFLICT (user_id, activity_id) DO NOTHING
  `,

  unsaveActivity: 'DELETE FROM saved_activities WHERE user_id = $1 AND activity_id = $2',

  getUserSavedActivities: `
    SELECT a.*, sa.saved_at 
    FROM activities a 
    JOIN saved_activities sa ON a.id = sa.activity_id 
    WHERE sa.user_id = $1 
    ORDER BY sa.saved_at DESC
  `,

  // Activity history
  addActivityHistory: `
    INSERT INTO activity_history (user_id, activity_id, rating) 
    VALUES ($1, $2, $3) 
    RETURNING *
  `,

  getUserActivityHistory: `
    SELECT a.*, ah.completed_at, ah.rating 
    FROM activities a 
    JOIN activity_history ah ON a.id = ah.activity_id 
    WHERE ah.user_id = $1 
    ORDER BY ah.completed_at DESC
    LIMIT $2 OFFSET $3
  `
};

// Database statistics
const getStats = async () => {
  try {
    const stats = await query(`
      SELECT 
        schemaname,
        tablename,
        attname,
        n_distinct,
        correlation
      FROM pg_stats 
      WHERE schemaname = 'public'
    `);

    const poolStats = {
      totalCount: pool.totalCount,
      idleCount: pool.idleCount,
