const aiQueue = new Queue('ai processing', {
  redis: {
    port: process.env.REDIS_PORT || 6379,
    host: process.env.REDIS_HOST || 'localhost',
    password: process.env.REDIS_PASSWORD || undefined,
  },
  defaultJobOptions: {
    removeOnComplete: 5,
    removeOnFail: 3,
    attempts: 2,
    backoff: {
      type: 'exponential',
      delay: 3000,
    },
  }
});

const analyticsQueue = new Queue('analytics processing', {
  redis: {
    port: process.env.REDIS_PORT || 6379,
    host: process.env.REDIS_HOST || 'localhost',
    password: process.env.REDIS_PASSWORD || undefined,
  },
  defaultJobOptions: {
    removeOnComplete: 20,
    removeOnFail: 10,
    attempts: 1,
  }
});

// Email job processors
emailQueue.process('welcome-email', async (job) => {
  const { userEmail, userName } = job.data;
  console.log(`Sending welcome email to ${userEmail}`);

  // TODO: Implement actual email sending logic
  // This would integrate with SendGrid, Mailgun, etc.

  return { sent: true, email: userEmail };
});

emailQueue.process('password-reset', async (job) => {
  const { userEmail, resetToken } = job.data;
  console.log(`Sending password reset email to ${userEmail}`);

  // TODO: Implement password reset email

  return { sent: true, email: userEmail };
});

// AI job processors
aiQueue.process('generate-activity', async (job) => {
  const { prompt, userId, preferences } = job.data;
  console.log(`Generating AI activity for user ${userId}`);

  try {
    // TODO: Implement AI activity generation
    // This would call the Gemini API

    const activity = {
      title: 'AI Generated Activity',
      description: 'This would be generated by AI',
      // ... other fields
    };

    // Log AI usage
    await query(
      'INSERT INTO ai_usage_logs (provider, model, prompt, user_id) VALUES ($1, $2, $3, $4)',
      ['gemini', 'gemini-pro', prompt, userId]
    );

    return activity;
  } catch (error) {
    console.error('AI generation failed:', error);
    throw error;
  }
});

// Analytics job processors
analyticsQueue.process('track-user-activity', async (job) => {
  const { userId, activityType, metadata } = job.data;

  try {
    // TODO: Implement analytics tracking
    // This could send data to Google Analytics, Mixpanel, etc.

    console.log(`Tracking ${activityType} for user ${userId}`);

    return { tracked: true, userId, activityType };
  } catch (error) {
    console.error('Analytics tracking failed:', error);
    throw error;
  }
});

analyticsQueue.process('update-user-stats', async (job) => {
  const { userId } = job.data;

  try {
    // Update user's monthly activity count
    const result = await query(`
      UPDATE users 
      SET monthly_activity_count = (
        SELECT COUNT(*) 
        FROM activity_history 
        WHERE user_id = $1 
        AND DATE_TRUNC('month', completed_at) = DATE_TRUNC('month', CURRENT_DATE)
      )
      WHERE id = $1
      RETURNING monthly_activity_count
    `, [userId]);

    return { userId, activityCount: result.rows[0]?.monthly_activity_count || 0 };
  } catch (error) {
    console.error('User stats update failed:', error);
    throw error;
  }
});

// Queue monitoring and error handling
const setupQueueMonitoring = () => {
  [emailQueue, aiQueue, analyticsQueue].forEach(queue => {
    queue.on('completed', (job, result) => {
      console.log(`Job ${job.id} completed:`, result);
    });

    queue.on('failed', (job, err) => {
      console.error(`Job ${job.id} failed:`, err.message);
    });

    queue.on('stalled', (job) => {
      console.warn(`Job ${job.id} stalled`);
    });
  });
};

// Queue management functions
const addEmailJob = (type, data, options = {}) => {
  return emailQueue.add(type, data, options);
};

const addAIJob = (type, data, options = {}) => {
  return aiQueue.add(type, data, options);
};

const addAnalyticsJob = (type, data, options = {}) => {
  return analyticsQueue.add(type, data, options);
};

const getQueueStats = async () => {
  const stats = {};

  for (const [name, queue] of Object.entries({ email: emailQueue, ai: aiQueue, analytics: analyticsQueue })) {
    stats[name] = {
      waiting: await queue.getWaiting(),
      active: await queue.getActive(),
      completed: await queue.getCompleted(),
      failed: await queue.getFailed(),
      delayed: await queue.getDelayed()
    };
  }

  return stats;
};

module.exports = {
  emailQueue,
  aiQueue,
  analyticsQueue,
  addEmailJob,
  addAIJob,
  addAnalyticsJob,
  getQueueStats,
  setupQueueMonitoring
};

