const winston = require('winston');
const path = require('path');

// Custom format for log messages
const logFormat = winston.format.combine(
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss'
  }),
  winston.format.errors({ stack: true }),
  winston.format.json(),
  winston.format.prettyPrint()
);

// Create logger instance
const logger = winston.createLogger({
  level: process.env.LOG_LEVEL || 'info',
  format: logFormat,
  defaultMeta: { 
    service: 'kido-backend',
    version: process.env.npm_package_version || '1.0.0'
  },
  transports: [
    // Write all logs to console
    new winston.transports.Console({
      format: winston.format.combine(
        winston.format.colorize(),
        winston.format.simple()
      )
    })
  ]
});

// Add file transports in production
if (process.env.NODE_ENV === 'production') {
  // Error logs
  logger.add(new winston.transports.File({
    filename: path.join(__dirname, '../../logs/error.log'),
    level: 'error',
    maxsize: 5242880, // 5MB
    maxFiles: 5
  }));

  // Combined logs
  logger.add(new winston.transports.File({
    filename: path.join(__dirname, '../../logs/combined.log'),
    maxsize: 5242880, // 5MB
    maxFiles: 5
  }));
}

// Create request logger middleware
const requestLogger = (req, res, next) => {
  const start = Date.now();

  res.on('finish', () => {
    const duration = Date.now() - start;
    const logData = {
      method: req.method,
      url: req.originalUrl,
      statusCode: res.statusCode,
      duration,
      userAgent: req.get('user-agent'),
      ip: req.ip || req.connection.remoteAddress
    };

    if (req.user) {
      logData.userId = req.user.id;
    }

    if (res.statusCode >= 400) {
      logger.error('HTTP Request Error', logData);
    } else if (duration > 1000) {
      logger.warn('Slow HTTP Request', logData);
    } else {
      logger.info('HTTP Request', logData);
    }
  });

  next();
};

// Database query logger
const queryLogger = (query, params, duration) => {
  if (duration > 1000) {
    logger.warn('Slow Database Query', {
      query: query.slice(0, 100) + (query.length > 100 ? '...' : ''),
      duration,
      params: params?.slice(0, 5) // Log only first 5 params for security
    });
  }
};

// Error logger with stack trace
const errorLogger = (error, context = {}) => {
  logger.error('Application Error', {
    message: error.message,
    stack: error.stack,
    ...context
  });
};

// Security event logger
const securityLogger = (event, details = {}) => {
  logger.warn('Security Event', {
    event,
    timestamp: new Date().toISOString(),
    ...details
  });
};

// Performance metrics logger
const performanceLogger = (metric, value, tags = {}) => {
  logger.info('Performance Metric', {
    metric,
    value,
    tags,
    timestamp: new Date().toISOString()
  });
};

// AI usage logger
const aiLogger = (provider, model, tokens, userId, success = true) => {
  logger.info('AI API Usage', {
    provider,
    model,
    tokens,
    userId,
    success,
    timestamp: new Date().toISOString()
  });
};

module.exports = {
  logger,
  requestLogger,
  queryLogger,
  errorLogger,
  securityLogger,
  performanceLogger,
  aiLogger
};

