// Enhanced health check endpoint
app.get('/api/health', async (req, res) => {
  const { healthCheck: dbHealth } = require('./services/database');
  const { isConnected: cacheConnected } = require('./services/cache');
  const { getQueueStats } = require('./services/queue');

  try {
    const dbStatus = await dbHealth();
    const queueStats = await getQueueStats();

    const health = {
      status: 'OK',
      timestamp: new Date().toISOString(),
      version: process.env.npm_package_version || '1.0.0',
      uptime: process.uptime(),
      memory: process.memoryUsage(),
      database: dbStatus,
      cache: {
        connected: cacheConnected()
      },
      queues: queueStats
    };

    res.json(health);
  } catch (error) {
    logger.error('Health check failed', error);
    res.status(503).json({
      status: 'ERROR',
      timestamp: new Date().toISOString(),
      error: error.message
    });
  }
});

// Error handling middleware
app.use(errorHandler);

// Initialize services and start server
const startServer = async () => {
  try {
    logger.info('Initializing Kid√≥ backend services...');

    // Initialize database
    await initDatabase();
    logger.info('Database initialized successfully');

    // Initialize Redis cache
    await initCache();
    logger.info('Cache service initialized');

    // Setup queue monitoring
    setupQueueMonitoring();
    logger.info('Queue monitoring setup complete');

    // Start server
    const server = app.listen(PORT, '0.0.0.0', () => {
      logger.info(`Kid√≥ backend server running on port ${PORT}`);
    });

    // Graceful shutdown
    process.on('SIGTERM', () => {
      logger.info('SIGTERM received, shutting down gracefully');
      server.close(() => {
        logger.info('Server closed');
        process.exit(0);
      });
    });

    process.on('SIGINT', () => {
      logger.info('SIGINT received, shutting down gracefully');
      server.close(() => {
        logger.info('Server closed');
        process.exit(0);
      });
    });

  } catch (error) {
    logger.error('Failed to start server:', error);
    process.exit(1);
  }
};

startServer();

