const { Server } = require('socket.io');
const jwt = require('jsonwebtoken');
const { logger } = require('./logger');
const { addAnalyticsJob } = require('./queue');

let io;

// Initialize WebSocket server
const initWebSocket = (server) => {
  io = new Server(server, {
    cors: {
      origin: process.env.CORS_ORIGIN || "http://localhost:3000",
      methods: ["GET", "POST"],
      credentials: true
    },
    pingTimeout: 60000,
    pingInterval: 25000
  });

  // Authentication middleware for Socket.IO
  io.use((socket, next) => {
    try {
      const token = socket.handshake.auth.token || socket.handshake.headers.authorization?.split(' ')[1];

      if (!token) {
        return next(new Error('Authentication error: No token provided'));
      }

      const decoded = jwt.verify(token, process.env.JWT_SECRET);
      socket.userId = decoded.id;
      socket.userEmail = decoded.email;

      logger.info('WebSocket authentication successful', { 
        userId: decoded.id, 
        socketId: socket.id 
      });

      next();
    } catch (error) {
      logger.error('WebSocket authentication failed', { 
        error: error.message, 
        socketId: socket.id 
      });
      next(new Error('Authentication error'));
    }
  });

  // Connection handling
  io.on('connection', (socket) => {
    logger.info('User connected via WebSocket', { 
      userId: socket.userId, 
      socketId: socket.id 
    });

    // Join user-specific room
    socket.join(`user_${socket.userId}`);

    // Track connection analytics
    addAnalyticsJob('track-user-activity', {
      userId: socket.userId,
      activityType: 'websocket_connect',
      metadata: { socketId: socket.id }
    });

    // Handle AI chat messages
    socket.on('ai_chat_message', async (data) => {
      try {
        const { message, conversationId } = data;

        logger.info('AI chat message received', { 
          userId: socket.userId, 
          conversationId,
          messageLength: message.length 
        });

        // Emit typing indicator
        socket.emit('ai_typing', { conversationId });

        // TODO: Process AI message (integrate with existing AI route logic)
        // This would call the AI service and return a response

        // Simulate AI processing time
        setTimeout(() => {
          socket.emit('ai_response', {
            conversationId,
            message: 'AI response would go here',
            timestamp: new Date().toISOString()
          });
        }, 1000);

        // Track AI usage
        addAnalyticsJob('track-user-activity', {
          userId: socket.userId,
          activityType: 'ai_chat_message',
          metadata: { conversationId, messageLength: message.length }
        });

      } catch (error) {
        logger.error('AI chat message error', { 
          userId: socket.userId, 
          error: error.message 
        });
        socket.emit('error', { message: 'Failed to process AI message' });
      }
    });

    // Handle activity interactions
    socket.on('activity_save', async (data) => {
      try {
        const { activityId } = data;

        // Broadcast to user's other sessions
        socket.to(`user_${socket.userId}`).emit('activity_saved', {
          activityId,
          timestamp: new Date().toISOString()
        });

        // Track activity save
        addAnalyticsJob('track-user-activity', {
          userId: socket.userId,
          activityType: 'activity_save',
          metadata: { activityId }
        });

        logger.info('Activity saved via WebSocket', { 
          userId: socket.userId, 
          activityId 
        });

      } catch (error) {
        logger.error('Activity save error', { 
          userId: socket.userId, 
          error: error.message 
        });
      }
    });

    socket.on('activity_complete', async (data) => {
      try {
        const { activityId, rating } = data;

        // Broadcast to user's other sessions
        socket.to(`user_${socket.userId}`).emit('activity_completed', {
          activityId,
          rating,
          timestamp: new Date().toISOString()
        });

        // Track activity completion
        addAnalyticsJob('track-user-activity', {
          userId: socket.userId,
          activityType: 'activity_complete',
          metadata: { activityId, rating }
        });

        // Update user stats
        addAnalyticsJob('update-user-stats', { userId: socket.userId });

        logger.info('Activity completed via WebSocket', { 
          userId: socket.userId, 
          activityId, 
          rating 
        });

      } catch (error) {
        logger.error('Activity complete error', { 
          userId: socket.userId, 
          error: error.message 
        });
      }
    });

    // Handle real-time notifications
    socket.on('mark_notification_read', async (data) => {
      try {
        const { notificationId } = data;

        // TODO: Mark notification as read in database

        socket.to(`user_${socket.userId}`).emit('notification_read', {
          notificationId,
          timestamp: new Date().toISOString()
        });

        logger.info('Notification marked as read', { 
          userId: socket.userId, 
          notificationId 
        });

      } catch (error) {
        logger.error('Mark notification read error', { 
          userId: socket.userId, 
          error: error.message 
        });
      }
    });

    // Handle disconnect
    socket.on('disconnect', (reason) => {
      logger.info('User disconnected from WebSocket', { 
        userId: socket.userId, 
        socketId: socket.id, 
        reason 
      });

      // Track disconnection analytics
      addAnalyticsJob('track-user-activity', {
        userId: socket.userId,
        activityType: 'websocket_disconnect',
        metadata: { socketId: socket.id, reason }
      });
    });

    // Handle connection errors
    socket.on('error', (error) => {
      logger.error('WebSocket error', { 
        userId: socket.userId, 
        socketId: socket.id, 
        error: error.message 
      });
    });
  });

  logger.info('WebSocket server initialized');
  return io;
};

// Helper functions to emit events to specific users or all users
const emitToUser = (userId, event, data) => {
  if (io) {
    io.to(`user_${userId}`).emit(event, data);
  }
};

const emitToAll = (event, data) => {
  if (io) {
    io.emit(event, data);
  }
};

// Notification system
const sendNotification = (userId, notification) => {
  emitToUser(userId, 'notification', {
    ...notification,
    timestamp: new Date().toISOString()
  });

  logger.info('Notification sent via WebSocket', { 
    userId, 
    notificationType: notification.type 
  });
};

// Activity recommendation system
const sendActivityRecommendation = (userId, activities) => {
  emitToUser(userId, 'activity_recommendation', {
    activities,
    timestamp: new Date().toISOString()
  });

  logger.info('Activity recommendation sent', { 
    userId, 
    activityCount: activities.length 
  });
};

// System status updates
const broadcastSystemStatus = (status) => {
  emitToAll('system_status', {
    ...status,
    timestamp: new Date().toISOString()
  });
};

module.exports = {
  initWebSocket,
  emitToUser,
  emitToAll,
  sendNotification,
  sendActivityRecommendation,
  broadcastSystemStatus
};

