const { query, transaction } = require('./database');
const { logger } = require('./logger');
const { addEmailJob } = require('./queue');

// Subscription plans configuration
const PLANS = {
  free: {
    id: 'free',
    name: 'Free Plan',
    price: 0,
    currency: 'EUR',
    interval: null,
    features: {
      activities_per_month: 5,
      ai_generations_per_month: 2,
      saved_activities: 10,
      basic_support: true,
      premium_content: false,
      advanced_ai: false,
      export_activities: false
    }
  },
  premium: {
    id: 'premium',
    name: 'Premium Plan',
    price: 9.99,
    currency: 'EUR',
    interval: 'month',
    features: {
      activities_per_month: -1, // unlimited
      ai_generations_per_month: -1, // unlimited
      saved_activities: -1, // unlimited
      basic_support: true,
      premium_content: true,
      advanced_ai: true,
      export_activities: true,
      priority_support: true,
      custom_activities: true
    }
  }
};

// Get user's subscription details
const getUserSubscription = async (userId) => {
  try {
    const result = await query(`
      SELECT 
        u.plan_type,
        u.monthly_activity_count,
        s.stripe_customer_id,
        s.stripe_subscription_id,
        s.status,
        s.current_period_start,
        s.current_period_end,
        s.created_at,
        s.updated_at
      FROM users u
      LEFT JOIN subscriptions s ON u.id = s.user_id
      WHERE u.id = $1
    `, [userId]);

    if (result.rows.length === 0) {
      throw new Error('User not found');
    }

    const user = result.rows[0];
    const plan = PLANS[user.plan_type] || PLANS.free;

    return {
      userId,
      plan: {
        ...plan,
        current: user.plan_type
      },
      subscription: {
        stripeCustomerId: user.stripe_customer_id,
        stripeSubscriptionId: user.stripe_subscription_id,
        status: user.status,
        currentPeriodStart: user.current_period_start,
        currentPeriodEnd: user.current_period_end
      },
      usage: {
        activitiesThisMonth: user.monthly_activity_count || 0,
        activitiesLimit: plan.features.activities_per_month
      }
    };
  } catch (error) {
    logger.error('Failed to get user subscription', { userId, error: error.message });
    throw error;
  }
};

// Check if user can perform action based on plan limits
const checkPlanLimit = async (userId, action) => {
  try {
    const subscription = await getUserSubscription(userId);
    const { plan, usage } = subscription;

    switch (action) {
      case 'view_activity':
        if (plan.features.activities_per_month === -1) return true; // unlimited
        return usage.activitiesThisMonth < plan.features.activities_per_month;

      case 'generate_ai_activity':
        // TODO: Track AI generations separately
        return plan.features.ai_generations_per_month === -1 || 
               plan.features.ai_generations_per_month > 0;

      case 'save_activity':
        if (plan.features.saved_activities === -1) return true; // unlimited

        // Check current saved count
        const savedResult = await query(
          'SELECT COUNT(*) FROM saved_activities WHERE user_id = $1',
          [userId]
        );
        const savedCount = parseInt(savedResult.rows[0].count);
        return savedCount < plan.features.saved_activities;

      case 'access_premium_content':
        return plan.features.premium_content;

      case 'use_advanced_ai':
        return plan.features.advanced_ai;

      case 'export_activities':
        return plan.features.export_activities;

      default:
        return false;
    }
  } catch (error) {
    logger.error('Failed to check plan limit', { userId, action, error: error.message });
    return false; // Fail closed for security
  }
};

// Upgrade user to premium plan
const upgradeToPremium = async (userId, stripeCustomerId, stripeSubscriptionId) => {
  try {
    await transaction(async (client) => {
      // Update user plan
      await client.query(
        'UPDATE users SET plan_type = $1, updated_at = CURRENT_TIMESTAMP WHERE id = $2',
        ['premium', userId]
      );

      // Create or update subscription record
      await client.query(`
        INSERT INTO subscriptions (user_id, stripe_customer_id, stripe_subscription_id, status, current_period_start, current_period_end)
        VALUES ($1, $2, $3, 'active', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP + INTERVAL '1 month')
        ON CONFLICT (user_id)
        DO UPDATE SET
          stripe_customer_id = $2,
          stripe_subscription_id = $3,
          status = 'active',
          current_period_start = CURRENT_TIMESTAMP,
          current_period_end = CURRENT_TIMESTAMP + INTERVAL '1 month',
          updated_at = CURRENT_TIMESTAMP
      `, [userId, stripeCustomerId, stripeSubscriptionId]);
    });

    // Send welcome email for premium
    addEmailJob('premium-welcome', {
      userId,
      planName: PLANS.premium.name
    });

    logger.info('User upgraded to premium', { userId, stripeCustomerId });

    return await getUserSubscription(userId);
  } catch (error) {
    logger.error('Failed to upgrade user to premium', { userId, error: error.message });
    throw error;
  }
};

// Downgrade user to free plan
const downgradeToFree = async (userId) => {
  try {
    await transaction(async (client) => {
      // Update user plan
      await client.query(
        'UPDATE users SET plan_type = $1, updated_at = CURRENT_TIMESTAMP WHERE id = $2',
        ['free', userId]
      );

      // Update subscription status
      await client.query(
        'UPDATE subscriptions SET status = $1, updated_at = CURRENT_TIMESTAMP WHERE user_id = $2',
        ['cancelled', userId]
      );
    });

    logger.info('User downgraded to free plan', { userId });

    return await getUserSubscription(userId);
  } catch (error) {
    logger.error('Failed to downgrade user to free', { userId, error: error.message });
    throw error;
  }
};

// Update subscription status (webhook handler)
const updateSubscriptionStatus = async (stripeSubscriptionId, status, currentPeriodEnd) => {
  try {
    const result = await query(
      'SELECT user_id FROM subscriptions WHERE stripe_subscription_id = $1',
      [stripeSubscriptionId]
    );

    if (result.rows.length === 0) {
      throw new Error(`Subscription not found: ${stripeSubscriptionId}`);
    }

    const userId = result.rows[0].user_id;

    await transaction(async (client) => {
      // Update subscription status
      await client.query(`
        UPDATE subscriptions 
        SET status = $1, current_period_end = $2, updated_at = CURRENT_TIMESTAMP 
        WHERE stripe_subscription_id = $3
      `, [status, new Date(currentPeriodEnd * 1000), stripeSubscriptionId]);

      // Update user plan based on subscription status
      const newPlanType = ['active', 'trialing'].includes(status) ? 'premium' : 'free';
      await client.query(
        'UPDATE users SET plan_type = $1, updated_at = CURRENT_TIMESTAMP WHERE id = $2',
        [newPlanType, userId]
      );
    });

    // Send notification emails based on status
    if (status === 'past_due') {
      addEmailJob('payment-failed', { userId });
    } else if (status === 'canceled') {
      addEmailJob('subscription-cancelled', { userId });
    }

    logger.info('Subscription status updated', { 
      userId, 
      stripeSubscriptionId, 
      status 
    });

    return { userId, status };
  } catch (error) {
    logger.error('Failed to update subscription status', { 
      stripeSubscriptionId, 
      status, 
      error: error.message 
    });
    throw error;
  }
};

// Get usage statistics for admin dashboard
const getUsageStatistics = async () => {
  try {
    const stats = await query(`
      SELECT 
        plan_type,
        COUNT(*) as user_count,
        AVG(monthly_activity_count) as avg_monthly_activities,
        SUM(monthly_activity_count) as total_monthly_activities
      FROM users 
      GROUP BY plan_type
    `);

    const subscriptionStats = await query(`
      SELECT 
        status,
        COUNT(*) as subscription_count
      FROM subscriptions 
      GROUP BY status
    `);

    return {
      usersByPlan: stats.rows,
      subscriptionsByStatus: subscriptionStats.rows,
      timestamp: new Date().toISOString()
    };
  } catch (error) {
    logger.error('Failed to get usage statistics', { error: error.message });
    throw error;
  }
};

// Middleware to check plan limits
const requiresPlanFeature = (feature) => {
  return async (req, res, next) => {
    try {
      if (!req.user || !req.user.id) {
        return res.status(401).json({ error: 'Authentication required' });
      }

      const hasAccess = await checkPlanLimit(req.user.id, feature);

      if (!hasAccess) {
        const subscription = await getUserSubscription(req.user.id);
        return res.status(403).json({ 
          error: 'Plan limit exceeded',
          plan: subscription.plan.current,
          feature,
          upgradeRequired: subscription.plan.current === 'free'
        });
      }

      next();
    } catch (error) {
      logger.error('Plan feature check failed', { 
        userId: req.user?.id, 
        feature, 
        error: error.message 
      });
      res.status(500).json({ error: 'Failed to check plan limits' });
    }
  };
};

module.exports = {
  PLANS,
  getUserSubscription,
  checkPlanLimit,
  upgradeToPremium,
  downgradeToFree,
  updateSubscriptionStatus,
  getUsageStatistics,
  requiresPlanFeature
};

