const { Pool } = require('pg');

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: false
});

const initDatabase = async () => {
  try {
    // Test connection
    await pool.query('SELECT NOW()');
    console.log('Database connection established');

    // Create tables if they don't exist
    await createTables();

    // Seed initial data if needed
    await seedData();

    return true;
  } catch (error) {
    console.error('Database initialization error:', error);
    throw error;
  }
};

const createTables = async () => {
  const client = await pool.connect();

  try {
    await client.query('BEGIN');

    // Users table
    await client.query(`
      CREATE TABLE IF NOT EXISTS users (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        name VARCHAR(255) NOT NULL,
        email VARCHAR(255) UNIQUE NOT NULL,
        password VARCHAR(255) NOT NULL,
        child_name VARCHAR(255),
        child_age INTEGER,
        plan_type VARCHAR(50) DEFAULT 'free' CHECK (plan_type IN ('free', 'premium')),
        monthly_activity_count INTEGER DEFAULT 0,
        role VARCHAR(50) DEFAULT 'user' CHECK (role IN ('user', 'admin')),
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    // Activities table
    await client.query(`
      CREATE TABLE IF NOT EXISTS activities (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        title VARCHAR(255) NOT NULL,
        description TEXT NOT NULL,
        instructions TEXT NOT NULL,
        category VARCHAR(100) NOT NULL,
        age_min INTEGER NOT NULL,
        age_max INTEGER NOT NULL,
        duration INTEGER NOT NULL,
        difficulty VARCHAR(50) DEFAULT 'Media' CHECK (difficulty IN ('Facile', 'Media', 'Difficile')),
        materials JSONB,
        image_url VARCHAR(500),
        language VARCHAR(5) DEFAULT 'it' CHECK (language IN ('it', 'en', 'fr', 'es', 'ru')),
        is_ai_generated BOOLEAN DEFAULT false,
        ai_provider VARCHAR(50),
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    // User saved activities
    await client.query(`
      CREATE TABLE IF NOT EXISTS saved_activities (
        user_id UUID REFERENCES users(id) ON DELETE CASCADE,
        activity_id UUID REFERENCES activities(id) ON DELETE CASCADE,
        saved_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        PRIMARY KEY (user_id, activity_id)
      )
    `);

    // Activity history
    await client.query(`
      CREATE TABLE IF NOT EXISTS activity_history (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id UUID REFERENCES users(id) ON DELETE CASCADE,
        activity_id UUID REFERENCES activities(id) ON DELETE CASCADE,
        completed_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        rating INTEGER CHECK (rating >= 1 AND rating <= 5)
      )
    `);

    // Chat conversations for AI assistant
    await client.query(`
      CREATE TABLE IF NOT EXISTS conversations (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        user_id UUID REFERENCES users(id) ON DELETE CASCADE,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    // Conversation messages
    await client.query(`
      CREATE TABLE IF NOT EXISTS conversation_messages (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        conversation_id UUID REFERENCES conversations(id) ON DELETE CASCADE,
        role VARCHAR(20) NOT NULL CHECK (role IN ('user', 'assistant')),
        content TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    // AI usage logs
    await client.query(`
      CREATE TABLE IF NOT EXISTS ai_usage_logs (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        provider VARCHAR(50) NOT NULL,
        model VARCHAR(100),
        prompt TEXT,
        tokens INTEGER DEFAULT 0,
        output TEXT,
        user_id UUID REFERENCES users(id) ON DELETE SET NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    await client.query('COMMIT');
    console.log('Database tables created successfully');
  } catch (error) {
    await client.query('ROLLBACK');
    throw error;
  } finally {
    client.release();
  }
};

const seedData = async () => {
  const client = await pool.connect();

  try {
    // Check if activities already exist
    const result = await client.query('SELECT COUNT(*) FROM activities');
    if (result.rows[0].count > 0) {
      console.log('Activities already seeded');
      return;
    }

    await client.query('BEGIN');

    // Seed initial activities
    const activities = [
      {
        title: 'Collage degli animali',
        description: 'Crea simpatici animali ritagliando e incollando forme colorate',
        instructions: 'Ritaglia forme geometriche dai fogli colorati. Combina le forme per creare animali. Incolla le forme su un foglio bianco. Aggiungi dettagli con i pennarelli.',
        age_min: 3,
