const Stripe = require('stripe');
const { logger } = require('./logger');
const { updateSubscriptionStatus } = require('./subscription');

const stripe = Stripe(process.env.STRIPE_SECRET_KEY);

// Product and price IDs (these would be created in Stripe Dashboard)
const STRIPE_CONFIG = {
  products: {
    premium: {
      productId: process.env.STRIPE_PREMIUM_PRODUCT_ID || 'prod_premium_kido',
      priceId: process.env.STRIPE_PREMIUM_PRICE_ID || 'price_premium_monthly'
    }
  },
  webhookSecret: process.env.STRIPE_WEBHOOK_SECRET
};

// Create or retrieve Stripe customer
const createCustomer = async (user) => {
  try {
    const customer = await stripe.customers.create({
      email: user.email,
      name: user.name,
      metadata: {
        userId: user.id,
        childName: user.child_name || '',
        childAge: user.child_age?.toString() || ''
      }
    });

    logger.info('Stripe customer created', { 
      userId: user.id, 
      customerId: customer.id 
    });

    return customer;
  } catch (error) {
    logger.error('Failed to create Stripe customer', { 
      userId: user.id, 
      error: error.message 
    });
    throw error;
  }
};

// Create checkout session for premium subscription
const createCheckoutSession = async (userId, userEmail, successUrl, cancelUrl) => {
  try {
    const session = await stripe.checkout.sessions.create({
      customer_email: userEmail,
      payment_method_types: ['card'],
      mode: 'subscription',
      line_items: [
        {
          price: STRIPE_CONFIG.products.premium.priceId,
          quantity: 1,
        },
      ],
      success_url: successUrl,
      cancel_url: cancelUrl,
      metadata: {
        userId: userId
      },
      subscription_data: {
        metadata: {
          userId: userId
        }
      },
      allow_promotion_codes: true,
      billing_address_collection: 'auto',
      tax_id_collection: {
        enabled: true
      }
    });

    logger.info('Stripe checkout session created', { 
      userId, 
      sessionId: session.id 
    });

    return session;
  } catch (error) {
    logger.error('Failed to create checkout session', { 
      userId, 
      error: error.message 
    });
    throw error;
  }
};

// Create customer portal session
const createPortalSession = async (customerId, returnUrl) => {
  try {
    const session = await stripe.billingPortal.sessions.create({
      customer: customerId,
      return_url: returnUrl,
    });

    logger.info('Stripe portal session created', { 
      customerId, 
      sessionId: session.id 
    });

    return session;
  } catch (error) {
    logger.error('Failed to create portal session', { 
      customerId, 
      error: error.message 
    });
    throw error;
  }
};

// Handle webhook events
const handleWebhook = async (rawBody, signature) => {
  try {
    const event = stripe.webhooks.constructEvent(
      rawBody,
      signature,
      STRIPE_CONFIG.webhookSecret
    );

    logger.info('Stripe webhook received', { 
      type: event.type, 
      id: event.id 
    });

    switch (event.type) {
      case 'customer.subscription.created':
        await handleSubscriptionCreated(event.data.object);
        break;

      case 'customer.subscription.updated':
        await handleSubscriptionUpdated(event.data.object);
        break;

      case 'customer.subscription.deleted':
        await handleSubscriptionDeleted(event.data.object);
        break;

      case 'invoice.payment_succeeded':
        await handlePaymentSucceeded(event.data.object);
        break;

      case 'invoice.payment_failed':
        await handlePaymentFailed(event.data.object);
        break;

      case 'checkout.session.completed':
        await handleCheckoutCompleted(event.data.object);
        break;

      default:
        logger.info('Unhandled webhook event type', { type: event.type });
    }

    return { received: true };
  } catch (error) {
    logger.error('Webhook handling failed', { error: error.message });
    throw error;
  }
};

// Webhook handlers
const handleSubscriptionCreated = async (subscription) => {
  try {
    const userId = subscription.metadata.userId;

    if (!userId) {
      logger.warn('Subscription created without userId metadata', { 
        subscriptionId: subscription.id 
      });
      return;
    }

    await updateSubscriptionStatus(
      subscription.id,
      subscription.status,
      subscription.current_period_end
    );

    logger.info('Subscription created and processed', { 
      userId, 
      subscriptionId: subscription.id 
    });
  } catch (error) {
    logger.error('Failed to handle subscription created', { 
      subscriptionId: subscription.id, 
      error: error.message 
    });
  }
};

const handleSubscriptionUpdated = async (subscription) => {
  try {
    await updateSubscriptionStatus(
      subscription.id,
      subscription.status,
      subscription.current_period_end
    );

    logger.info('Subscription updated and processed', { 
      subscriptionId: subscription.id,
      status: subscription.status 
    });
  } catch (error) {
    logger.error('Failed to handle subscription updated', { 
      subscriptionId: subscription.id, 
      error: error.message 
    });
  }
};

const handleSubscriptionDeleted = async (subscription) => {
  try {
    await updateSubscriptionStatus(
      subscription.id,
      'canceled',
      subscription.current_period_end
    );

    logger.info('Subscription deleted and processed', { 
      subscriptionId: subscription.id 
    });
  } catch (error) {
    logger.error('Failed to handle subscription deleted', { 
      subscriptionId: subscription.id, 
      error: error.message 
    });
  }
};

const handlePaymentSucceeded = async (invoice) => {
  try {
    if (invoice.subscription) {
      const subscription = await stripe.subscriptions.retrieve(invoice.subscription);

      await updateSubscriptionStatus(
        subscription.id,
        subscription.status,
        subscription.current_period_end
      );

      logger.info('Payment succeeded, subscription updated', { 
        subscriptionId: subscription.id,
        amount: invoice.amount_paid / 100,
        currency: invoice.currency 
      });
    }
  } catch (error) {
    logger.error('Failed to handle payment succeeded', { 
      invoiceId: invoice.id, 
      error: error.message 
    });
  }
};

const handlePaymentFailed = async (invoice) => {
  try {
    if (invoice.subscription) {
      const subscription = await stripe.subscriptions.retrieve(invoice.subscription);

      await updateSubscriptionStatus(
        subscription.id,
        'past_due',
        subscription.current_period_end
      );

      logger.warn('Payment failed, subscription marked as past due', { 
        subscriptionId: subscription.id,
        attemptCount: invoice.attempt_count 
      });
    }
  } catch (error) {
    logger.error('Failed to handle payment failed', { 
      invoiceId: invoice.id, 
      error: error.message 
    });
  }
};

const handleCheckoutCompleted = async (session) => {
  try {
    const userId = session.metadata.userId;

    if (!userId) {
      logger.warn('Checkout completed without userId metadata', { 
        sessionId: session.id 
      });
      return;
    }

    if (session.mode === 'subscription') {
      const subscription = await stripe.subscriptions.retrieve(session.subscription);

      await updateSubscriptionStatus(
        subscription.id,
        subscription.status,
        subscription.current_period_end
      );

      logger.info('Checkout completed, subscription activated', { 
        userId,
        sessionId: session.id,
        subscriptionId: subscription.id 
      });
    }
  } catch (error) {
    logger.error('Failed to handle checkout completed', { 
      sessionId: session.id, 
      error: error.message 
    });
  }
};

// Get subscription details from Stripe
const getSubscriptionDetails = async (subscriptionId) => {
  try {
    const subscription = await stripe.subscriptions.retrieve(subscriptionId, {
      expand: ['latest_invoice', 'customer']
    });

    return {
      id: subscription.id,
      status: subscription.status,
      currentPeriodStart: new Date(subscription.current_period_start * 1000),
      currentPeriodEnd: new Date(subscription.current_period_end * 1000),
      cancelAtPeriodEnd: subscription.cancel_at_period_end,
      customer: subscription.customer,
      latestInvoice: subscription.latest_invoice
    };
  } catch (error) {
    logger.error('Failed to get subscription details', { 
      subscriptionId, 
      error: error.message 
    });
    throw error;
  }
};

// Cancel subscription
const cancelSubscription = async (subscriptionId, immediate = false) => {
  try {
    const subscription = await stripe.subscriptions.update(subscriptionId, {
      cancel_at_period_end: !immediate
    });

    if (immediate) {
      await stripe.subscriptions.cancel(subscriptionId);
    }

    logger.info('Subscription cancellation requested', { 
      subscriptionId,
      immediate,
      cancelAtPeriodEnd: subscription.cancel_at_period_end 
    });

    return subscription;
  } catch (error) {
    logger.error('Failed to cancel subscription', { 
      subscriptionId, 
      error: error.message 
    });
    throw error;
  }
};

module.exports = {
  createCustomer,
  createCheckoutSession,
  createPortalSession,
  handleWebhook,
  getSubscriptionDetails,
  cancelSubscription,
  STRIPE_CONFIG
};

