const express = require('express');
const { body, validationResult } = require('express-validator');
const { authenticateToken } = require('../middleware/auth');
const { 
  createCheckoutSession, 
  createPortalSession, 
  handleWebhook,
  cancelSubscription 
} = require('../services/stripe');
const { 
  getUserSubscription, 
  upgradeToPremium, 
  downgradeToFree,
  requiresPlanFeature
} = require('../services/subscription');
const { logger } = require('../services/logger');

const router = express.Router();

// Get current user's subscription details
router.get('/subscription', authenticateToken, async (req, res) => {
  try {
    const subscription = await getUserSubscription(req.user.id);
    res.json(subscription);
  } catch (error) {
    logger.error('Failed to get subscription details', { 
      userId: req.user.id, 
      error: error.message 
    });
    res.status(500).json({ error: 'Failed to get subscription details' });
  }
});

// Create Stripe checkout session for premium subscription
router.post('/create-checkout', [
  authenticateToken,
  body('successUrl').isURL().withMessage('Valid success URL is required'),
  body('cancelUrl').isURL().withMessage('Valid cancel URL is required')
], async (req, res) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({ errors: errors.array() });
    }

    const { successUrl, cancelUrl } = req.body;

    // Check if user is already premium
    const subscription = await getUserSubscription(req.user.id);
    if (subscription.plan.current === 'premium') {
      return res.status(400).json({ 
        error: 'User already has premium subscription' 
      });
    }

    const session = await createCheckoutSession(
      req.user.id,
      req.user.email,
      successUrl,
      cancelUrl
    );

    res.json({ 
      sessionId: session.id,
      url: session.url 
    });

  } catch (error) {
    logger.error('Failed to create checkout session', { 
      userId: req.user.id, 
      error: error.message 
    });
    res.status(500).json({ error: 'Failed to create checkout session' });
  }
});

// Create customer portal session
router.post('/create-portal', [
  authenticateToken,
  body('returnUrl').isURL().withMessage('Valid return URL is required')
], async (req, res) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({ errors: errors.array() });
    }

    const { returnUrl } = req.body;
    const subscription = await getUserSubscription(req.user.id);

    if (!subscription.subscription.stripeCustomerId) {
      return res.status(400).json({ 
        error: 'No Stripe customer found for user' 
      });
    }

    const session = await createPortalSession(
      subscription.subscription.stripeCustomerId,
      returnUrl
    );

    res.json({ url: session.url });

  } catch (error) {
    logger.error('Failed to create portal session', { 
      userId: req.user.id, 
      error: error.message 
    });
