require('dotenv').config();
const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const rateLimit = require('express-rate-limit');
const slowDown = require('express-slow-down');
const compression = require('compression');
const cookieParser = require('cookie-parser');

const authRoutes = require('./routes/auth');
const userRoutes = require('./routes/users');
const activityRoutes = require('./routes/activities');
const aiRoutes = require('./routes/ai');
const { errorHandler } = require('./middleware/errorHandler');
const { initDatabase } = require('./database/init');
const { initCache } = require('./services/cache');
const { setupQueueMonitoring } = require('./services/queue');
const { logger, requestLogger } = require('./services/logger');
const { initWebSocket } = require('./services/websocket');

const app = express();
const PORT = process.env.PORT || 3001;

// Trust proxy for accurate IP addresses
app.set('trust proxy', 1);

// Request logging
app.use(requestLogger);

// Compression middleware
app.use(compression());

// Security middleware
app.use(helmet({
  contentSecurityPolicy: false, // Allow for development
  crossOriginEmbedderPolicy: false
}));

app.use(cors({
  origin: process.env.CORS_ORIGIN || 'http://localhost:3000',
  credentials: true
}));

// Enhanced rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: (req) => {
    // Different limits for different user types
    if (req.user && req.user.plan_type === 'premium') {
      return 200; // Premium users get higher limits
    }
    return 100; // Free users limit
  },
  standardHeaders: true,
  legacyHeaders: false,
  message: {
    error: 'Too many requests from this IP, please try again later.',
    retryAfter: 15 * 60
  }
});

// Slow down repeated requests
const speedLimiter = slowDown({
  windowMs: 15 * 60 * 1000, // 15 minutes
  delayAfter: 50, // Allow 50 requests per 15 minutes at full speed
  delayMs: 500, // Add 500ms of delay per request after delayAfter
  maxDelayMs: 20000, // Maximum delay of 20 seconds
});

app.use('/api/', limiter);
app.use('/api/', speedLimiter);

// Body parsing middleware
app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(cookieParser());

// Routes
app.use('/api/auth', authRoutes);
app.use('/api/users', userRoutes);
app.use('/api/activities', activityRoutes);
app.use('/api/ai', aiRoutes);

// Enhanced health check endpoint
app.get('/api/health', async (req, res) => {
  const { healthCheck: dbHealth } = require('./services/database');
  const { isConnected: cacheConnected } = require('./services/cache');
  const { getQueueStats } = require('./services/queue');

  try {
    const dbStatus = await dbHealth();
    const queueStats = await getQueueStats();

    const health = {
      status: 'OK',
      timestamp: new Date().toISOString(),
      version: process.env.npm_package_version || '1.0.0',
      uptime: process.uptime(),
      memory: process.memoryUsage(),
      database: dbStatus,
      cache: {
        connected: cacheConnected()
      },
      queues: queueStats
    };

    res.json(health);
  } catch (error) {
    logger.error('Health check failed', error);
    res.status(503).json({
      status: 'ERROR',
      timestamp: new Date().toISOString(),
      error: error.message
    });
  }
});

// Error handling middleware
app.use(errorHandler);

// Initialize services and start server
const startServer = async () => {
  try {
    logger.info('Initializing Kid√≥ backend services...');

    // Initialize database
    await initDatabase();
    logger.info('Database initialized successfully');

    // Initialize Redis cache
    await initCache();
    logger.info('Cache service initialized');

    // Setup queue monitoring
    setupQueueMonitoring();
    logger.info('Queue monitoring setup complete');

    // Start server
    const server = app.listen(PORT, '0.0.0.0', () => {
      logger.info(`Kid√≥ backend server running on port ${PORT}`);
    });

    // Initialize WebSocket server
    initWebSocket(server);
    logger.info('WebSocket server initialized');

    // Graceful shutdown
    process.on('SIGTERM', () => {
      logger.info('SIGTERM received, shutting down gracefully');
      server.close(() => {
        logger.info('Server closed');
        process.exit(0);
      });
    });

    process.on('SIGINT', () => {
      logger.info('SIGINT received, shutting down gracefully');
      server.close(() => {
        logger.info('Server closed');
        process.exit(0);
      });
    });

  } catch (error) {
    logger.error('Failed to start server:', error);
    process.exit(1);
  }
};

startServer();

