const { GoogleGenerativeAI } = require('@google/generative-ai');
const { query } = require('./database');
const { logger, aiLogger } = require('./logger');
const { get: getCache, set: setCache, keys: cacheKeys } = require('./cache');
const { checkPlanLimit } = require('./subscription');

const genAI = new GoogleGenerativeAI(process.env.GEMINI_API_KEY);

// Premium AI models and configurations
const AI_MODELS = {
  free: {
    model: 'gemini-pro',
    maxTokens: 1000,
    temperature: 0.7,
    features: ['basic_generation']
  },
  premium: {
    model: 'gemini-pro-vision', // If available, otherwise fallback to gemini-pro
    maxTokens: 2000,
    temperature: 0.8,
    features: ['advanced_generation', 'image_analysis', 'personalized_learning', 'detailed_instructions']
  }
};

// Enhanced activity generation with premium features
const generateAdvancedActivity = async (userId, preferences, planType = 'free') => {
  try {
    // Check plan limits
    const canGenerate = await checkPlanLimit(userId, 'generate_ai_activity');
    if (!canGenerate) {
      throw new Error('AI generation limit exceeded for current plan');
    }

    const modelConfig = AI_MODELS[planType] || AI_MODELS.free;
    const model = genAI.getGenerativeModel({ model: modelConfig.model });

    // Build enhanced prompt based on plan type
    let prompt = buildEnhancedPrompt(preferences, planType);

    // Check cache first
    const cacheKey = cacheKeys.aiResponse(prompt + planType);
    const cachedResult = await getCache(cacheKey);

    if (cachedResult) {
      logger.info('AI activity served from cache', { userId, planType });
      return cachedResult;
    }

    // Generate content
    const result = await model.generateContent(prompt);
    const response = await result.response;
    const text = response.text();

    // Parse and enhance the response
    const activity = parseAIResponse(text, preferences, planType);

    // Add premium enhancements
    if (planType === 'premium') {
      activity.premiumFeatures = await addPremiumEnhancements(activity, preferences);
    }

    // Cache the result
    const cacheTimeout = planType === 'premium' ? 7200 : 3600; // Premium cache longer
    await setCache(cacheKey, activity, cacheTimeout);

    // Log AI usage
    aiLogger('gemini', modelConfig.model, text.length, userId, true);

    // Save to database
    await saveAIActivity(activity, userId);

    logger.info('AI activity generated successfully', { 
      userId, 
      planType, 
      activityTitle: activity.title 
    });

    return activity;

  } catch (error) {
    logger.error('Failed to generate AI activity', { 
      userId, 
      planType, 
      error: error.message 
    });

    aiLogger('gemini', AI_MODELS[planType]?.model || 'gemini-pro', 0, userId, false);
    throw error;
  }
};

// Build enhanced prompt based on plan features
const buildEnhancedPrompt = (preferences, planType) => {
  const {
    childAge,
    childName,
    interests,
    availableTime,
    materials,
    difficulty,
    language = 'it',
    learningGoals,
    specialNeeds
  } = preferences;

  let prompt = `
Genera un'attivit√† educativa per bambini in lingua ${language} con queste specifiche:

BAMBINO:
- Et√†: ${childAge} anni
- Nome: ${childName || 'il bambino'}
${interests ? `- Interessi: ${interests.join(', ')}` : ''}
${learningGoals ? `- Obiettivi di apprendimento: ${learningGoals.join(', ')}` : ''}
${specialNeeds ? `- Esigenze speciali: ${specialNeeds}` : ''}

REQUISITI:
- Tempo disponibile: ${availableTime} minuti
- Materiali disponibili: ${materials ? materials.join(', ') : 'materiali di base'}
- Difficolt√†: ${difficulty || 'media'}

