# Proxy cache configuration
proxy_cache_path /var/cache/nginx levels=1:2 keys_zone=STATIC:10m inactive=7d use_temp_path=off;

# DNS resolver for external domains
resolver 8.8.8.8 8.8.4.4 valid=300s;
resolver_timeout 5s;

# Upstream PHP-FPM
upstream php-fpm {
    server php:9000;
}

# Server block for procurato.local
server {
    listen 80;
    listen [::]:80;

    server_name procurato.local www.procurato.local;

    root /var/www/html;
    index index.php index.html index.htm;

    # Client body size for uploads
    client_max_body_size 100M;

    # Gzip compression
    gzip on;
    gzip_vary on;
    gzip_proxied any;
    gzip_comp_level 6;
    gzip_types text/plain text/css text/xml text/javascript application/json application/javascript application/xml+rss application/rss+xml image/svg+xml;

    # Security headers
    add_header X-Frame-Options "SAMEORIGIN" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header X-XSS-Protection "1; mode=block" always;

    # Logs
    access_log /var/log/nginx/access.log;
    error_log /var/log/nginx/error.log;

    # Cache static assets
    location ~* \.(jpg|jpeg|png|gif|ico|css|js|pdf|doc|docx|ppt|pptx|xls|xlsx|ttf|woff|woff2|eot|svg|webp)$ {
        expires 30d;
        add_header Cache-Control "public, immutable";

        # Try local file first, then proxy to staging if not found
        try_files $uri @staging_assets;
    }

    # Proxy missing uploads to staging
    location @staging_assets {
        proxy_pass https://procurato.nwdesigns.it$request_uri;
        proxy_set_header Host procurato.nwdesigns.it;
        proxy_ssl_server_name on;
        proxy_cache STATIC;
        proxy_cache_valid 200 7d;
        proxy_cache_use_stale error timeout invalid_header updating http_500 http_502 http_503 http_504;
        add_header X-Cache-Status $upstream_cache_status;
    }

    # Special handling for wp-content/uploads - proxy to staging if not found locally
    location ~ ^/wp-content/uploads/(.*)$ {
        try_files $uri @staging_uploads;
    }

    location @staging_uploads {
        proxy_pass https://procurato.nwdesigns.it$request_uri;
        proxy_set_header Host procurato.nwdesigns.it;
        proxy_ssl_server_name on;
        proxy_ssl_verify off;
        proxy_cache STATIC;
        proxy_cache_valid 200 30d;
        proxy_cache_use_stale error timeout invalid_header updating http_500 http_502 http_503 http_504;
        add_header X-Cache-Status $upstream_cache_status;

        # Additional headers for better compatibility
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto https;
        proxy_http_version 1.1;
    }

    # Deny access to sensitive files
    location ~ /\. {
        deny all;
    }

    location ~ /\.ht {
        deny all;
    }

    location = /favicon.ico {
        log_not_found off;
        access_log off;
    }

    location = /robots.txt {
        allow all;
        log_not_found off;
        access_log off;
    }

    # WordPress specific rules
    location / {
        try_files $uri $uri/ /index.php?$args;
    }

    # PHP handling
    location ~ \.php$ {
        try_files $uri =404;
        fastcgi_split_path_info ^(.+\.php)(/.+)$;
        fastcgi_pass php-fpm;
        fastcgi_index index.php;
        include fastcgi_params;
        fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
        fastcgi_param PATH_INFO $fastcgi_path_info;

        # PHP-FPM tuning
        fastcgi_buffer_size 128k;
        fastcgi_buffers 256 16k;
        fastcgi_busy_buffers_size 256k;
        fastcgi_temp_file_write_size 256k;
        fastcgi_read_timeout 300;
    }

    # WordPress admin area
    location ~ ^/(wp-admin|wp-login\.php) {
        try_files $uri $uri/ /index.php?$args;

        location ~ \.php$ {
            try_files $uri =404;
            fastcgi_split_path_info ^(.+\.php)(/.+)$;
            fastcgi_pass php-fpm;
            fastcgi_index index.php;
            include fastcgi_params;
            fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
            fastcgi_param PATH_INFO $fastcgi_path_info;
            fastcgi_read_timeout 300;
        }
    }

    # Deny access to xmlrpc.php
    location = /xmlrpc.php {
        deny all;
    }

    # Elementor specific
    location ~ ^/wp-content/uploads/elementor/ {
        add_header Cache-Control "public, max-age=31536000, immutable";
    }
}

