# FastCGI cache configuration
fastcgi_cache_path /var/cache/nginx levels=1:2 keys_zone=WORDPRESS:100m inactive=60m;
fastcgi_cache_key "$scheme$request_method$host$request_uri";

server {
    listen 80;
    server_name www.procurato.local procurato.local;

    root /var/www/html;
    index index.php index.html index.htm;

    # Security headers
    add_header X-Frame-Options "SAMEORIGIN" always;
    add_header X-XSS-Protection "1; mode=block" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header Referrer-Policy "no-referrer-when-downgrade" always;
    add_header Content-Security-Policy "default-src * data: 'unsafe-eval' 'unsafe-inline'" always;

    # Disable access to sensitive files
    location ~* /(?:uploads|files)/.*\.php$ {
        deny all;
    }

    # WordPress security - block access to sensitive files
    location ~* /(?:\.htaccess|\.htpasswd|readme\.html|readme\.txt|license\.txt) {
        deny all;
    }

    # Block access to wp-config.php
    location ~* wp-config\.php {
        deny all;
    }

    # Block access to xmlrpc.php (prevents brute force attacks)
    location = /xmlrpc.php {
        deny all;
    }

    # Rate limit wp-login.php
    location = /wp-login.php {
        limit_req zone=login burst=2 nodelay;
        include fastcgi.conf;
        fastcgi_pass php:9000;
    }

    # Rate limit wp-admin
    location ~* /wp-admin/ {
        limit_req zone=wp_admin burst=10 nodelay;
        try_files $uri $uri/ /index.php?$args;
    }

    # Static files caching
    location ~* \.(jpg|jpeg|gif|png|css|js|ico|svg|woff|woff2|ttf|eot)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
        access_log off;

        # Try to serve static files directly, fallback to WordPress
        try_files $uri $uri/ /index.php?$args;
    }

    # Handle favicon
    location = /favicon.ico {
        log_not_found off;
        access_log off;
        expires 1y;
        add_header Cache-Control "public, immutable";
    }

    # Handle robots.txt
    location = /robots.txt {
        log_not_found off;
        access_log off;
    }

    # WordPress permalinks
    location / {
        try_files $uri $uri/ /index.php?$args;
    }

    # PHP processing with FastCGI cache
    location ~ \.php$ {
        include fastcgi.conf;
        fastcgi_pass php:9000;

        # FastCGI cache settings
        fastcgi_cache WORDPRESS;
        fastcgi_cache_valid 200 301 302 60m;
        fastcgi_cache_valid 404 1m;
        fastcgi_cache_min_uses 1;
        fastcgi_cache_use_stale error timeout invalid_header http_500;
        fastcgi_cache_bypass $skip_cache;
        fastcgi_no_cache $skip_cache;

        # Add cache status header
        add_header X-FastCGI-Cache $upstream_cache_status;

        # Skip cache for certain conditions
        set $skip_cache 0;

        # POST requests and urls with a query string should always go to PHP
        if ($request_method = POST) {
            set $skip_cache 1;
        }
        if ($query_string != "") {
            set $skip_cache 1;
        }

        # Don't cache uris containing the following segments
        if ($request_uri ~* "/wp-admin/|/xmlrpc.php|wp-.*.php|/feed/|index.php|sitemap(_index)?.xml") {
            set $skip_cache 1;
        }

        # Don't use the cache for logged in users or recent commenters
        if ($http_cookie ~* "comment_author|wordpress_[a-f0-9]+|wp-postpass|wordpress_no_cache|wordpress_logged_in") {
            set $skip_cache 1;
        }
    }

    # Cache purge endpoint (for plugins like WP Rocket)
    location ~ /purge(/.*) {
        fastcgi_cache_purge WORDPRESS "$scheme$request_method$host$1";
    }

    # Health check endpoint
    location = /health {
        access_log off;
        return 200 "healthy\n";
        add_header Content-Type text/plain;
    }

    # Deny access to . files
    location ~ /\. {
        deny all;
        access_log off;
        log_not_found off;
    }
}
