// Inizializzazione con ottimizzazioni
function initImprovedScrollers() {
    // Inject styles if not present
    if (!document.getElementById('improved-scroller-styles')) {
        document.head.insertAdjacentHTML('beforeend', improvedStyles);
    }

    // Find all scrollable containers
    const containers = document.querySelectorAll('.scrollable-container');

    containers.forEach(container => {
        // Skip if already initialized
        if (container.hasAttribute('data-improved-scroller')) return;

        // Initialize only if there's horizontal overflow
        if (container.scrollWidth > container.clientWidth) {
            new ImprovedElementorScroller(container);
            container.setAttribute('data-improved-scroller', 'true');
        }
    });
}

// Initialize when DOM is ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initImprovedScrollers);
} else {
    initImprovedScrollers();
}

// Elementor integration
if (typeof jQuery !== 'undefined') {
