   553→        const scrollAmount = this.isWideScreen 
   554→            ? this.container.clientWidth * 0.5 
   555→            : this.container.clientWidth * 0.8;
   556→        this.smoothScroll(scrollAmount);
   557→        this.announce('Scorrimento a destra');
   558→    }
