/**
 * Slider Orizzontale Migliorato per Elementor
 * Versione 3.0 - Ottimizzato per Mobile, Desktop e Wide Screen
 */

// Classe principale per gestire lo slider
class ImprovedElementorScroller {
    constructor(container) {
        this.container = container;
        this.wrapper = null;
        this.isScrolling = false;
        this.scrollTimeout = null;
        this.isMobile = window.innerWidth <= 768;
        this.isTablet = window.innerWidth > 768 && window.innerWidth <= 1024;
        this.isWideScreen = window.innerWidth > 1920;

        // Feature detection
        this.supportsPassive = this.checkPassiveSupport();
        this.supportsScrollBehavior = 'scrollBehavior' in document.documentElement.style;
        this.supportsTouch = 'ontouchstart' in window || navigator.maxTouchPoints > 0;

        // Inizializza
        this.init();

        // Setup responsive handler
        this.setupResponsiveHandler();
    }

    // Check supporto passive listeners
    checkPassiveSupport() {
        let supportsPassive = false;
        try {
            const opts = Object.defineProperty({}, 'passive', {
                get: () => { supportsPassive = true; }
            });
            window.addEventListener('testPassive', null, opts);
            window.removeEventListener('testPassive', null, opts);
        } catch (e) {}
        return supportsPassive;
    }

    init() {
        // Crea wrapper per aggiungere controlli senza modificare HTML
        this.wrapContainer();

        // Setup features
        this.setupNavButtons();
        this.setupKeyboard();
        this.setupMouseWheel();
        this.setupTouch();
        this.setupScrollIndicator();
        this.setupAccessibility();
        this.setupSnapPoints();

        // Aggiorna stato iniziale
        this.updateUI();
    }

    // Setup responsive handler
    setupResponsiveHandler() {
        let resizeTimer;
        window.addEventListener('resize', () => {
            clearTimeout(resizeTimer);
            resizeTimer = setTimeout(() => {
                const wasMobile = this.isMobile;
                const wasTablet = this.isTablet;
                const wasWideScreen = this.isWideScreen;

                this.isMobile = window.innerWidth <= 768;
                this.isTablet = window.innerWidth > 768 && window.innerWidth <= 1024;
                this.isWideScreen = window.innerWidth > 1920;

                // Re-init if device type changed
                if (wasMobile !== this.isMobile || wasTablet !== this.isTablet || wasWideScreen !== this.isWideScreen) {
                    this.updateResponsiveFeatures();
                }
            }, 250);
        });
    }

    updateResponsiveFeatures() {
        // Update navigation visibility
        if (this.prevButton && this.nextButton) {
            if (this.isMobile) {
                this.prevButton.style.display = 'none';
                this.nextButton.style.display = 'none';
            } else {
                this.updateUI();
            }
        }

        // Update snap behavior
        this.setupSnapPoints();
    }

    // Wrap container esistente per aggiungere controlli
    wrapContainer() {
        // Se già wrappato, esci
        if (this.container.parentElement.classList.contains('elementor-scroller-wrapper')) {
            this.wrapper = this.container.parentElement;
            return;
        }

        // Crea wrapper
        this.wrapper = document.createElement('div');
        this.wrapper.className = 'elementor-scroller-wrapper';
        this.wrapper.style.position = 'relative';

        // Inserisci wrapper
        this.container.parentNode.insertBefore(this.wrapper, this.container);
        this.wrapper.appendChild(this.container);
    }

    // Setup pulsanti navigazione
    setupNavButtons() {
        // Non mostrare pulsanti su mobile
        if (this.isMobile) return;

        // Crea pulsanti solo se non esistono
        if (!this.wrapper.querySelector('.nav-button')) {
            // Pulsante precedente
            const prevButton = document.createElement('button');
            prevButton.className = 'nav-button nav-prev';
            prevButton.innerHTML = `
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M15 18l-6-6 6-6"/>
                </svg>
            `;
            prevButton.setAttribute('aria-label', 'Scorri a sinistra');
            prevButton.addEventListener('click', () => this.scrollPrev());

            // Pulsante successivo
            const nextButton = document.createElement('button');
            nextButton.className = 'nav-button nav-next';
            nextButton.innerHTML = `
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M9 18l6-6-6-6"/>
                </svg>
            `;
            nextButton.setAttribute('aria-label', 'Scorri a destra');
            nextButton.addEventListener('click', () => this.scrollNext());

            // Aggiungi al wrapper
            this.wrapper.appendChild(prevButton);
            this.wrapper.appendChild(nextButton);

            // Store references
            this.prevButton = prevButton;
            this.nextButton = nextButton;
        }
    }

    // Setup snap points per mobile
    setupSnapPoints() {
        if (this.isMobile || this.isTablet) {
            // Enable CSS snap scrolling on mobile/tablet
            this.container.style.scrollSnapType = 'x mandatory';
            this.container.style.webkitOverflowScrolling = 'touch'; // Smooth iOS scrolling

            // Add snap points to children
            const children = this.container.children;
            for (let child of children) {
                child.style.scrollSnapAlign = 'start';
                child.style.scrollSnapStop = 'normal';
            }
        } else {
            // Disable snap scrolling on desktop
            this.container.style.scrollSnapType = '';
            this.container.style.webkitOverflowScrolling = '';

            const children = this.container.children;
            for (let child of children) {
                child.style.scrollSnapAlign = '';
                child.style.scrollSnapStop = '';
            }
        }
    }

    // Setup tastiera migliorato
    setupKeyboard() {
        this.container.addEventListener('keydown', (e) => {
            const scrollAmount = this.isMobile ? this.container.clientWidth : this.container.clientWidth * 0.8;
            let handled = true;

            // Aggiungi classe per smooth scroll da tastiera
            this.container.classList.add('keyboard-scrolling');

            switch(e.key) {
                case 'ArrowLeft':
                    this.smoothScroll(-scrollAmount);
                    break;
                case 'ArrowRight':
                    this.smoothScroll(scrollAmount);
                    break;
                case 'Home':
                    this.scrollToStart();
                    break;
                case 'End':
                    this.scrollToEnd();
                    break;
                default:
                    handled = false;
            }

            if (handled) {
                e.preventDefault();
                // Rimuovi classe dopo animazione
                setTimeout(() => {
                    this.container.classList.remove('keyboard-scrolling');
                }, 400);
            } else {
                this.container.classList.remove('keyboard-scrolling');
            }
        });
    }

    // Setup mouse wheel - MIGLIORATO per wide screen
    setupMouseWheel() {
        if (this.isMobile) return; // Skip on mobile

        let accumulatedDelta = 0;
        let wheelTimeout;
        let lastWheelTime = 0;

        const wheelHandler = (e) => {
            // Solo se container è in hover o focus
            if (!this.container.matches(':hover, :focus-within')) return;

            const now = Date.now();
            const timeDelta = now - lastWheelTime;
            lastWheelTime = now;

            // Prevent default vertical scroll
            e.preventDefault();

            // Calculate dynamic scroll speed based on screen width
            const baseSpeed = this.isWideScreen ? 1.5 : 1;
            const dynamicMultiplier = timeDelta < 100 ? 2 : 1; // Boost for quick scrolls

            // Accumula piccoli delta per scroll più fluido
            accumulatedDelta += e.deltaY * baseSpeed * dynamicMultiplier;

            clearTimeout(wheelTimeout);
            wheelTimeout = setTimeout(() => {
                if (Math.abs(accumulatedDelta) > 10) {
                    // Normalizza e applica scroll con limite massimo
                    const maxScroll = this.isWideScreen ? 600 : 400;
                    const scrollAmount = Math.sign(accumulatedDelta) * Math.min(Math.abs(accumulatedDelta), maxScroll);
                    this.smoothScroll(scrollAmount);
                }
                accumulatedDelta = 0;
            }, 50);
        };

        // Aggiungi listener con passive false
        this.container.addEventListener('wheel', wheelHandler,
            this.supportsPassive ? { passive: false } : false
        );
    }

    // Setup touch con momentum migliorato e supporto mobile
    setupTouch() {
        let startX = 0;
        let startY = 0;
        let scrollLeft = 0;
        let isDragging = false;
        let isHorizontalDrag = false;
        let animationId = null;
        let velocity = 0;
        let lastX = 0;
        let lastTime = 0;
        let touchStartTime = 0;
        let momentumHistory = [];

        const startDragging = (pageX, pageY) => {
            isDragging = true;
            isHorizontalDrag = false;
            startX = pageX;
            startY = pageY;
            lastX = pageX;
            scrollLeft = this.container.scrollLeft;
            lastTime = Date.now();
            touchStartTime = Date.now();
            velocity = 0;
            momentumHistory = [];

            // Cancella animazioni
            if (animationId) {
                cancelAnimationFrame(animationId);
            }

            // Add active class
            this.container.classList.add('active');

            // Disable snap during drag on mobile
            if (this.isMobile || this.isTablet) {
                this.container.style.scrollSnapType = 'none';
            }
        };

        const drag = (pageX, pageY, e) => {
            if (!isDragging) return;

            const deltaX = pageX - startX;
            const deltaY = pageY - startY;

            // Determina se è un movimento orizzontale
            if (!isHorizontalDrag && (Math.abs(deltaX) > 10 || Math.abs(deltaY) > 10)) {
                isHorizontalDrag = Math.abs(deltaX) > Math.abs(deltaY);
            }

            // Se è movimento orizzontale, previeni scroll verticale
            if (isHorizontalDrag && e) {
                e.preventDefault();
            }

            // Procedi solo se è movimento orizzontale
            if (isHorizontalDrag) {
                const currentTime = Date.now();
                const timeDelta = currentTime - lastTime;
                const distance = pageX - lastX;

                // Store momentum history for better velocity calculation
                momentumHistory.push({ distance, timeDelta, time: currentTime });

                // Keep only recent history (last 100ms)
                momentumHistory = momentumHistory.filter(h => currentTime - h.time < 100);

                // Calculate velocity from recent history
                if (momentumHistory.length > 1) {
                    let totalDistance = 0;
                    let totalTime = 0;
                    for (let i = 1; i < momentumHistory.length; i++) {
                        totalDistance += momentumHistory[i].distance;
                        totalTime += momentumHistory[i].timeDelta;
                    }
                    if (totalTime > 0) {
                        velocity = (totalDistance / totalTime) * 16; // Normalize to 60fps
                    }
                }

                // Apply different sensitivity for mobile
                const sensitivity = this.isMobile ? 1 : 1.5;
                const walk = deltaX * sensitivity;
                this.container.scrollLeft = scrollLeft - walk;

                lastX = pageX;
                lastTime = currentTime;
            }
        };

        const stopDragging = () => {
            if (!isDragging) return;

            isDragging = false;
            isHorizontalDrag = false;
            this.container.classList.remove('active');

            const dragDuration = Date.now() - touchStartTime;

            // Re-enable snap on mobile after drag
            if (this.isMobile || this.isTablet) {
                this.container.style.scrollSnapType = 'x mandatory';
            }

            // Apply momentum only for quick swipes
            if (Math.abs(velocity) > 2 && dragDuration < 300) {
                // Enhanced momentum for mobile
                const initialVelocity = this.isMobile ? velocity * 1.5 : velocity;
                const deceleration = this.isMobile ? 0.92 : 0.95;
                const minVelocity = this.isMobile ? 0.1 : 0.5;

                const animate = () => {
                    if (Math.abs(velocity) > minVelocity) {
                        this.container.scrollLeft -= velocity;
                        velocity *= deceleration;
                        animationId = requestAnimationFrame(animate);
                    } else {
                        this.updateUI();
                    }
                };

                velocity = initialVelocity;
                animate();
            } else {
                this.updateUI();
            }
        };

        // Mouse events (desktop only)
        if (!this.isMobile) {
            this.container.addEventListener('mousedown', (e) => {
                // Prevent text selection during drag
                e.preventDefault();
                startDragging(e.pageX, e.pageY);
            });

            document.addEventListener('mousemove', (e) => {
                drag(e.pageX, e.pageY);
            });

            document.addEventListener('mouseup', stopDragging);
        }

        // Touch events
        this.container.addEventListener('touchstart', (e) => {
            // Don't interfere with pinch zoom
            if (e.touches.length === 1) {
                startDragging(e.touches[0].pageX, e.touches[0].pageY);
            }
        }, { passive: true });

        this.container.addEventListener('touchmove', (e) => {
            if (e.touches.length === 1) {
                drag(e.touches[0].pageX, e.touches[0].pageY, e);
            }
        }, { passive: false });

        this.container.addEventListener('touchend', stopDragging, { passive: true });
        this.container.addEventListener('touchcancel', stopDragging, { passive: true });
    }

    // Setup indicatore di scroll
    setupScrollIndicator() {
        // Skip progress bar on mobile
        if (this.isMobile) return;

        // Crea progress bar solo se non nascosta da design
        if (!this.wrapper.classList.contains('hide-progress')) {
            const progressBar = document.createElement('div');
            progressBar.className = 'scroll-progress';
            progressBar.setAttribute('role', 'progressbar');
            progressBar.setAttribute('aria-label', 'Progresso scroll');
            progressBar.setAttribute('aria-valuemin', '0');
            progressBar.setAttribute('aria-valuemax', '100');

            this.wrapper.appendChild(progressBar);
            this.progressBar = progressBar;
        }

        // Ottimizza scroll listener con RAF
        let ticking = false;
        let lastScrollLeft = -1;

        this.container.addEventListener('scroll', () => {
            if (!ticking) {
                requestAnimationFrame(() => {
                    // Only update if scroll position actually changed
                    if (this.container.scrollLeft !== lastScrollLeft) {
                        lastScrollLeft = this.container.scrollLeft;
                        this.updateUI();
                    }
                    ticking = false;
                });
                ticking = true;
            }
        }, this.supportsPassive ? { passive: true } : false);
    }

    // Setup accessibilità
    setupAccessibility() {
        // ARIA labels
        this.container.setAttribute('role', 'region');
        this.container.setAttribute('aria-label', 'Contenuti scrollabili orizzontalmente');
        this.container.setAttribute('tabindex', '0'); // Make focusable

        // Live region per annunci
        const liveRegion = document.createElement('div');
        liveRegion.className = 'sr-only';
        liveRegion.setAttribute('role', 'status');
        liveRegion.setAttribute('aria-live', 'polite');
        liveRegion.setAttribute('aria-atomic', 'true');
        liveRegion.style.cssText = `
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0,0,0,0);
            white-space: nowrap;
            border: 0;
        `;

        this.wrapper.appendChild(liveRegion);
        this.liveRegion = liveRegion;
    }

    // Scroll fluido ottimizzato
    smoothScroll(distance) {
        if (this.supportsScrollBehavior) {
            this.container.scrollBy({
                left: distance,
                behavior: 'smooth'
            });
        } else {
            // Fallback animato
            this.animateScroll(this.container.scrollLeft + distance);
        }
    }

    // Animazione scroll per fallback con RAF optimization
    animateScroll(target) {
        const start = this.container.scrollLeft;
        const distance = target - start;

        // Skip animation for very small distances
        if (Math.abs(distance) < 1) {
            this.container.scrollLeft = target;
            this.updateUI();
            return;
        }

        const duration = Math.min(300, Math.abs(distance) * 0.5); // Dynamic duration
        let startTime = null;
        let animationId = null;

        const animation = (currentTime) => {
            if (!startTime) startTime = currentTime;
            const elapsed = currentTime - startTime;
            const progress = Math.min(elapsed / duration, 1);

            // Easing
            const easeInOutQuad = progress < 0.5
                ? 2 * progress * progress
                : 1 - Math.pow(-2 * progress + 2, 2) / 2;

            this.container.scrollLeft = start + distance * easeInOutQuad;

            if (progress < 1) {
                animationId = requestAnimationFrame(animation);
            } else {
                this.updateUI();
            }
        };

        animationId = requestAnimationFrame(animation);

        // Store animation ID for cancellation
        this.currentAnimation = animationId;
    }

    // Metodi di navigazione
    scrollPrev() {
        const scrollAmount = this.isWideScreen 
            ? this.container.clientWidth * 0.5 
            : this.container.clientWidth * 0.8;
        this.smoothScroll(-scrollAmount);
        this.announce('Scorrimento a sinistra');
    }

    scrollNext() {
        const scrollAmount = this.isWideScreen 
            ? this.container.clientWidth * 0.5 
            : this.container.clientWidth * 0.8;
        this.smoothScroll(scrollAmount);
        this.announce('Scorrimento a destra');
    }

    scrollToStart() {
        this.container.scrollTo({
            left: 0,
            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
        });
        this.announce('Inizio dei contenuti');
    }

    scrollToEnd() {
        const maxScroll = this.container.scrollWidth - this.container.clientWidth;
        this.container.scrollTo({
            left: maxScroll,
            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
        });
        this.announce('Fine dei contenuti');
    }

    // Aggiorna UI con ottimizzazioni
    updateUI() {
        const scrollLeft = this.container.scrollLeft;
        const maxScroll = this.container.scrollWidth - this.container.clientWidth;

        // Aggiorna pulsanti (desktop only)
        if (this.prevButton && this.nextButton && !this.isMobile) {
            const atStart = scrollLeft <= 0;
            const atEnd = scrollLeft >= maxScroll - 1;

            this.prevButton.disabled = atStart;
            this.nextButton.disabled = atEnd;

            // Update visibility
            this.prevButton.style.display = atStart ? 'none' : 'flex';
            this.nextButton.style.display = atEnd ? 'none' : 'flex';

            // Aggiorna ARIA
            this.prevButton.setAttribute('aria-disabled', atStart);
            this.nextButton.setAttribute('aria-disabled', atEnd);
        }

        // Aggiorna progress bar (desktop only)
        if (this.progressBar && maxScroll > 0 && !this.isMobile) {
            const progress = (scrollLeft / maxScroll) * 100;
            this.progressBar.style.width = `${progress}%`;
            this.progressBar.setAttribute('aria-valuenow', Math.round(progress));
        }
    }

    // Annuncia a screen reader
    announce(message) {
        if (this.liveRegion) {
            this.liveRegion.textContent = message;
            // Reset dopo annuncio
            setTimeout(() => {
                this.liveRegion.textContent = '';
            }, 1000);
        }
    }
}

// CSS da iniettare - Ottimizzato per mobile e wide screen
const improvedStyles = `
    <style id="improved-scroller-styles">
