    64→            resizeTimer = setTimeout(() => {
    65→                const wasMobile = this.isMobile;
    66→                const wasTablet = this.isTablet;
    67→                const wasWideScreen = this.isWideScreen;
    68→                
    69→                this.isMobile = window.innerWidth <= 768;
    70→                this.isTablet = window.innerWidth > 768 && window.innerWidth <= 1024;
    71→                this.isWideScreen = window.innerWidth > 1920;
    72→                
    73→                // Re-init if device type changed
    74→                if (wasMobile !== this.isMobile || wasTablet !== this.isTablet || wasWideScreen !== this.isWideScreen) {
    75→                    this.updateResponsiveFeatures();
