   217→    // Setup mouse wheel - MIGLIORATO per wide screen
   218→    setupMouseWheel() {
   219→        if (this.isMobile) return; // Skip on mobile
   220→        
   221→        let accumulatedDelta = 0;
   222→        let wheelTimeout;
   223→        let lastWheelTime = 0;
   224→
   225→        const wheelHandler = (e) => {
   226→            // Solo se container è in hover o focus
   227→            if (!this.container.matches(':hover, :focus-within')) return;
   228→
   229→            const now = Date.now();
   230→            const timeDelta = now - lastWheelTime;
   231→            lastWheelTime = now;
   232→
   233→            // Prevent default vertical scroll
   234→            e.preventDefault();
   235→
   236→            // Calculate dynamic scroll speed based on screen width
   237→            const baseSpeed = this.isWideScreen ? 1.5 : 1;
   238→            const dynamicMultiplier = timeDelta < 100 ? 2 : 1; // Boost for quick scrolls
   239→            
   240→            // Accumula piccoli delta per scroll più fluido
   241→            accumulatedDelta += e.deltaY * baseSpeed * dynamicMultiplier;
   242→
   243→            clearTimeout(wheelTimeout);
   244→            wheelTimeout = setTimeout(() => {
   245→                if (Math.abs(accumulatedDelta) > 10) {
   246→                    // Normalizza e applica scroll con limite massimo
   247→                    const maxScroll = this.isWideScreen ? 600 : 400;
   248→                    const scrollAmount = Math.sign(accumulatedDelta) * Math.min(Math.abs(accumulatedDelta), maxScroll);
   249→                    this.smoothScroll(scrollAmount);
   250→                }
   251→                accumulatedDelta = 0;
   252→            }, 50);
   253→        };
   254→
   255→        // Aggiungi listener con passive false
   256→        this.container.addEventListener('wheel', wheelHandler,
   257→            this.supportsPassive ? { passive: false } : false
   258→        );
   259→    }
