   261→    // Setup touch con momentum migliorato e supporto mobile
   262→    setupTouch() {
   263→        let startX = 0;
   264→        let startY = 0;
   265→        let scrollLeft = 0;
   266→        let isDragging = false;
   267→        let isHorizontalDrag = false;
   268→        let animationId = null;
   269→        let velocity = 0;
   270→        let lastX = 0;
   271→        let lastTime = 0;
   272→        let touchStartTime = 0;
   273→        let momentumHistory = [];
   274→
   275→        const startDragging = (pageX, pageY) => {
   276→            isDragging = true;
   277→            isHorizontalDrag = false;
   278→            startX = pageX;
   279→            startY = pageY;
   280→            lastX = pageX;
   281→            scrollLeft = this.container.scrollLeft;
   282→            lastTime = Date.now();
   283→            touchStartTime = Date.now();
   284→            velocity = 0;
   285→            momentumHistory = [];
   286→
   287→            // Cancella animazioni
   288→            if (animationId) {
   289→                cancelAnimationFrame(animationId);
   290→            }
   291→
   292→            // Add active class
   293→            this.container.classList.add('active');
   294→            
   295→            // Disable snap during drag on mobile
   296→            if (this.isMobile || this.isTablet) {
   297→                this.container.style.scrollSnapType = 'none';
   298→            }
   299→        };
   300→
   301→        const drag = (pageX, pageY, e) => {
   302→            if (!isDragging) return;
   303→
   304→            const deltaX = pageX - startX;
   305→            const deltaY = pageY - startY;
   306→            
   307→            // Determina se è un movimento orizzontale
   308→            if (!isHorizontalDrag && (Math.abs(deltaX) > 10 || Math.abs(deltaY) > 10)) {
   309→                isHorizontalDrag = Math.abs(deltaX) > Math.abs(deltaY);
   310→            }
   311→            
   312→            // Se è movimento orizzontale, previeni scroll verticale
   313→            if (isHorizontalDrag && e) {
   314→                e.preventDefault();
   315→            }
   316→
   317→            // Procedi solo se è movimento orizzontale
   318→            if (isHorizontalDrag) {
   319→                const currentTime = Date.now();
   320→                const timeDelta = currentTime - lastTime;
   321→                const distance = pageX - lastX;
   322→
   323→                // Store momentum history for better velocity calculation
   324→                momentumHistory.push({ distance, timeDelta, time: currentTime });
   325→                
   326→                // Keep only recent history (last 100ms)
   327→                momentumHistory = momentumHistory.filter(h => currentTime - h.time < 100);
   328→
   329→                // Calculate velocity from recent history
   330→                if (momentumHistory.length > 1) {
   331→                    let totalDistance = 0;
   332→                    let totalTime = 0;
   333→                    for (let i = 1; i < momentumHistory.length; i++) {
   334→                        totalDistance += momentumHistory[i].distance;
   335→                        totalTime += momentumHistory[i].timeDelta;
   336→                    }
   337→                    if (totalTime > 0) {
   338→                        velocity = (totalDistance / totalTime) * 16; // Normalize to 60fps
   339→                    }
   340→                }
   341→
   342→                // Apply different sensitivity for mobile
   343→                const sensitivity = this.isMobile ? 1 : 1.5;
   344→                const walk = deltaX * sensitivity;
   345→                this.container.scrollLeft = scrollLeft - walk;
   346→
   347→                lastX = pageX;
   348→                lastTime = currentTime;
   349→            }
   350→        };
   351→
   352→        const stopDragging = () => {
   353→            if (!isDragging) return;
   354→
   355→            isDragging = false;
   356→            isHorizontalDrag = false;
   357→            this.container.classList.remove('active');
   358→            
   359→            const dragDuration = Date.now() - touchStartTime;
   360→            
   361→            // Re-enable snap on mobile after drag
   362→            if (this.isMobile || this.isTablet) {
   363→                this.container.style.scrollSnapType = 'x mandatory';
   364→            }
   365→
   366→            // Apply momentum only for quick swipes
   367→            if (Math.abs(velocity) > 2 && dragDuration < 300) {
   368→                // Enhanced momentum for mobile
   369→                const initialVelocity = this.isMobile ? velocity * 1.5 : velocity;
   370→                const deceleration = this.isMobile ? 0.92 : 0.95;
   371→                const minVelocity = this.isMobile ? 0.1 : 0.5;
   372→
   373→                const animate = () => {
   374→                    if (Math.abs(velocity) > minVelocity) {
   375→                        this.container.scrollLeft -= velocity;
   376→                        velocity *= deceleration;
   377→                        animationId = requestAnimationFrame(animate);
   378→                    } else {
   379→                        this.updateUI();
   380→                    }
   381→                };
   382→
   383→                velocity = initialVelocity;
   384→                animate();
   385→            } else {
   386→                this.updateUI();
   387→            }
   388→        };
   389→
   390→        // Mouse events (desktop only)
   391→        if (!this.isMobile) {
   392→            this.container.addEventListener('mousedown', (e) => {
   393→                // Prevent text selection during drag
   394→                e.preventDefault();
   395→                startDragging(e.pageX, e.pageY);
   396→            });
   397→
   398→            document.addEventListener('mousemove', (e) => {
   399→                drag(e.pageX, e.pageY);
   400→            });
   401→
   402→            document.addEventListener('mouseup', stopDragging);
   403→        }
   404→
   405→        // Touch events
   406→        this.container.addEventListener('touchstart', (e) => {
   407→            // Don't interfere with pinch zoom
   408→            if (e.touches.length === 1) {
   409→                startDragging(e.touches[0].pageX, e.touches[0].pageY);
   410→            }
   411→        }, { passive: true });
   412→
   413→        this.container.addEventListener('touchmove', (e) => {
   414→            if (e.touches.length === 1) {
   415→                drag(e.touches[0].pageX, e.touches[0].pageY, e);
   416→            }
   417→        }, { passive: false });
   418→
   419→        this.container.addEventListener('touchend', stopDragging, { passive: true });
   420→        this.container.addEventListener('touchcancel', stopDragging, { passive: true });
   421→    }
