   440→
   441→        // Ottimizza scroll listener con RAF
   442→        let ticking = false;
   443→        let lastScrollLeft = -1;
   444→        
   445→        this.container.addEventListener('scroll', () => {
   446→            if (!ticking) {
   447→                requestAnimationFrame(() => {
   448→                    // Only update if scroll position actually changed
   449→                    if (this.container.scrollLeft !== lastScrollLeft) {
   450→                        lastScrollLeft = this.container.scrollLeft;
   451→                        this.updateUI();
   452→                    }
   453→                    ticking = false;
   454→                });
   455→                ticking = true;
   456→            }
