   489→    // Scroll fluido ottimizzato
   490→    smoothScroll(distance) {
   491→        if (this.supportsScrollBehavior) {
   492→            this.container.scrollBy({
   493→                left: distance,
   494→                behavior: 'smooth'
   495→            });
   496→        } else {
   497→            // Fallback animato
   498→            this.animateScroll(this.container.scrollLeft + distance);
   499→        }
   500→    }
   501→
   502→    // Animazione scroll per fallback con RAF optimization
   503→    animateScroll(target) {
   504→        const start = this.container.scrollLeft;
   505→        const distance = target - start;
   506→        
   507→        // Skip animation for very small distances
   508→        if (Math.abs(distance) < 1) {
   509→            this.container.scrollLeft = target;
   510→            this.updateUI();
   511→            return;
   512→        }
   513→        
   514→        const duration = Math.min(300, Math.abs(distance) * 0.5); // Dynamic duration
   515→        let startTime = null;
   516→        let animationId = null;
   517→
   518→        const animation = (currentTime) => {
   519→            if (!startTime) startTime = currentTime;
   520→            const elapsed = currentTime - startTime;
   521→            const progress = Math.min(elapsed / duration, 1);
   522→
   523→            // Easing
   524→            const easeInOutQuad = progress < 0.5
   525→                ? 2 * progress * progress
   526→                : 1 - Math.pow(-2 * progress + 2, 2) / 2;
   527→
   528→            this.container.scrollLeft = start + distance * easeInOutQuad;
   529→
   530→            if (progress < 1) {
   531→                animationId = requestAnimationFrame(animation);
   532→            } else {
   533→                this.updateUI();
   534→            }
   535→        };
   536→
   537→        animationId = requestAnimationFrame(animation);
   538→        
   539→        // Store animation ID for cancellation
   540→        this.currentAnimation = animationId;
   541→    }
