   543→    // Metodi di navigazione
   544→    scrollPrev() {
   545→        const scrollAmount = this.isWideScreen 
   546→            ? this.container.clientWidth * 0.5 
   547→            : this.container.clientWidth * 0.8;
   548→        this.smoothScroll(-scrollAmount);
   549→        this.announce('Scorrimento a sinistra');
   550→    }
   551→
   552→    scrollNext() {
   553→        const scrollAmount = this.isWideScreen 
   554→            ? this.container.clientWidth * 0.5 
   555→            : this.container.clientWidth * 0.8;
   556→        this.smoothScroll(scrollAmount);
   557→        this.announce('Scorrimento a destra');
   558→    }
