   560→    scrollToStart() {
   561→        this.container.scrollTo({
   562→            left: 0,
   563→            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
   564→        });
   565→        this.announce('Inizio dei contenuti');
   566→    }
   567→
   568→    scrollToEnd() {
   569→        const maxScroll = this.container.scrollWidth - this.container.clientWidth;
   570→        this.container.scrollTo({
   571→            left: maxScroll,
   572→            behavior: this.supportsScrollBehavior ? 'smooth' : 'auto'
   573→        });
   574→        this.announce('Fine dei contenuti');
   575→    }
