   577→    // Aggiorna UI con ottimizzazioni
   578→    updateUI() {
   579→        const scrollLeft = this.container.scrollLeft;
   580→        const maxScroll = this.container.scrollWidth - this.container.clientWidth;
   581→
   582→        // Aggiorna pulsanti (desktop only)
   583→        if (this.prevButton && this.nextButton && !this.isMobile) {
   584→            const atStart = scrollLeft <= 0;
   585→            const atEnd = scrollLeft >= maxScroll - 1;
   586→            
   587→            this.prevButton.disabled = atStart;
   588→            this.nextButton.disabled = atEnd;
   589→            
   590→            // Update visibility
   591→            this.prevButton.style.display = atStart ? 'none' : 'flex';
   592→            this.nextButton.style.display = atEnd ? 'none' : 'flex';
   593→
   594→            // Aggiorna ARIA
   595→            this.prevButton.setAttribute('aria-disabled', atStart);
   596→            this.nextButton.setAttribute('aria-disabled', atEnd);
   597→        }
   598→
   599→        // Aggiorna progress bar (desktop only)
   600→        if (this.progressBar && maxScroll > 0 && !this.isMobile) {
   601→            const progress = (scrollLeft / maxScroll) * 100;
   602→            this.progressBar.style.width = `${progress}%`;
   603→            this.progressBar.setAttribute('aria-valuenow', Math.round(progress));
   604→        }
   605→    }
   606→
   607→    // Annuncia a screen reader
   608→    announce(message) {
   609→        if (this.liveRegion) {
   610→            this.liveRegion.textContent = message;
   611→            // Reset dopo annuncio
   612→            setTimeout(() => {
   613→                this.liveRegion.textContent = '';
   614→            }, 1000);
   615→        }
   616→    }
   617→}
