    }

    // Aggiorna UI con ottimizzazioni
    updateUI() {
        const scrollLeft = this.container.scrollLeft;
        const maxScroll = this.container.scrollWidth - this.container.clientWidth;

        // Aggiorna pulsanti (desktop only)
        if (this.prevButton && this.nextButton && !this.isMobile) {
